import{_ as n,c as s,o as a,a as e}from"./app.14440598.js";const m=`{"title":"v4.0.54 Release Notes","description":"","frontmatter":{"title":"v4.0.54 Release Notes","slug":"v4-0-54"},"headers":[{"level":2,"title":"AutoQuery Viewer","slug":"autoquery-viewer"},{"level":3,"title":"Install ServiceStack.Admin","slug":"install-servicestack-admin"},{"level":3,"title":"Live Examples","slug":"live-examples"},{"level":3,"title":"Default Minimal UI","slug":"default-minimal-ui"},{"level":3,"title":"Marking up AutoQuery Services","slug":"marking-up-autoquery-services"},{"level":3,"title":"Filter AutoQuery Services","slug":"filter-autoquery-services"},{"level":3,"title":"Authorized Only Queries","slug":"authorized-only-queries"},{"level":3,"title":"Updated in Real-time","slug":"updated-in-real-time"},{"level":3,"title":"Multiple Conditions","slug":"multiple-conditions"},{"level":3,"title":"Change Content-Type","slug":"change-content-type"},{"level":3,"title":"Customize Columns","slug":"customize-columns"},{"level":3,"title":"Sorting Columns and Paging Results","slug":"sorting-columns-and-paging-results"},{"level":2,"title":"AutoQuery Enhancements","slug":"autoquery-enhancements"},{"level":3,"title":"Parameterized AutoQuery","slug":"parameterized-autoquery"},{"level":3,"title":"Customizable Fields","slug":"customizable-fields"},{"level":3,"title":"Multiple Conditions","slug":"multiple-conditions-1"},{"level":3,"title":"Named Connection","slug":"named-connection"},{"level":3,"title":"Generate AutoQuery Services from OrmLite T4 Templates","slug":"generate-autoquery-services-from-ormlite-t4-templates"},{"level":3,"title":"AdminFeature Rich UI Implementation","slug":"adminfeature-rich-ui-implementation"},{"level":2,"title":"Server Events","slug":"server-events"},{"level":3,"title":"Xamarin.Android Chat","slug":"xamarin-android-chat"},{"level":3,"title":"Networked Time Traveller Shape Creator","slug":"networked-time-traveller-shape-creator"},{"level":3,"title":"Updating Channels on Live Subscriptions","slug":"updating-channels-on-live-subscriptions"},{"level":3,"title":"New onUpdate Notification","slug":"new-onupdate-notification"},{"level":3,"title":".NET UpdateSubscriber APIs","slug":"net-updatesubscriber-apis"},{"level":3,"title":"JavaScript UpdateSubscriber APIs","slug":"javascript-updatesubscriber-apis"},{"level":3,"title":"ServerEvents Update Channel APIs","slug":"serverevents-update-channel-apis"},{"level":2,"title":"TypeScript React App (beta)","slug":"typescript-react-app-beta"},{"level":2,"title":"TypeScript Redux","slug":"typescript-redux"},{"level":2,"title":"ss-utils","slug":"ss-utils"},{"level":3,"title":"ss-utils now available on npm and DefinitelyTyped","slug":"ss-utils-now-available-on-npm-and-definitelytyped"},{"level":3,"title":"New ss-utils API's","slug":"new-ss-utils-api-s"},{"level":2,"title":"Customize JSON Responses on-the-fly","slug":"customize-json-responses-on-the-fly"},{"level":3,"title":"Camel Humps Notation","slug":"camel-humps-notation"},{"level":3,"title":"Custom JSON Live Example","slug":"custom-json-live-example"},{"level":3,"title":"Custom JSON Settings","slug":"custom-json-settings"},{"level":2,"title":"Improved support for Multitenancy","slug":"improved-support-for-multitenancy"},{"level":3,"title":"Change Database Connection at Runtime","slug":"change-database-connection-at-runtime"},{"level":3,"title":"ConnectionInfo Attribute","slug":"connectioninfo-attribute"},{"level":3,"title":"Multi Tenancy Example","slug":"multi-tenancy-example"},{"level":3,"title":"New CreateQuery overloads for Custom AutoQuery","slug":"new-createquery-overloads-for-custom-autoquery"},{"level":2,"title":"ServiceClient URL Resolvers","slug":"serviceclient-url-resolvers"},{"level":2,"title":"ServiceStack.Discovery.Consul","slug":"servicestack-discovery-consul"},{"level":2,"title":"Multiple File Uploads","slug":"multiple-file-uploads"},{"level":3,"title":"PCL WinStore Client retargeted to 8.1","slug":"pcl-winstore-client-retargeted-to-8-1"},{"level":3,"title":"Local MemoryCacheClient","slug":"local-memorycacheclient"},{"level":3,"title":"Cookies","slug":"cookies"},{"level":2,"title":"Redis","slug":"redis"},{"level":2,"title":"OrmLite","slug":"ormlite"},{"level":3,"title":"Select Fields","slug":"select-fields"},{"level":3,"title":"Other OrmLite Changes","slug":"other-ormlite-changes"},{"level":3,"title":"ServiceStack.Text","slug":"servicestack-text"},{"level":2,"title":"Stripe Gateway","slug":"stripe-gateway"},{"level":2,"title":"Minor ServiceStack Features","slug":"minor-servicestack-features"},{"level":2,"title":"Changes","slug":"changes"},{"level":2,"title":"WARNING .NET 4.0 builds will cease after August 1, 2016","slug":"warning-net-4-0-builds-will-cease-after-august-1-2016"}],"relativePath":"releases/v4.0.54.md","lastUpdated":1634495308434}`,t={},o=e(`__VP_STATIC_START__<p>We have another massive release in store with big updates to AutoQuery, Server Events and TypeScript support as well as greater flexibility for Multitenancy scenarios and around Service Clients.</p><h2 id="autoquery-viewer" tabindex="-1"><a href="https://github.com/ServiceStack/Admin" target="_blank" rel="noopener noreferrer">AutoQuery Viewer</a> <a class="header-anchor" href="#autoquery-viewer" aria-hidden="true">#</a></h2><p>If you&#39;ve yet to try <a href="/autoquery.html">Auto Query</a> we encourage you to check it out, it lets you effortlessly create high-performance, fully-queryable, self-descriptive services with just a single, typed Request DTO definition. As they&#39;re just normal ServiceStack Services they also benefit from ServiceStack&#39;s surrounding feature ecosystem, including native support in .NET PCL Service Clients and multi-language <a href="/add-servicestack-reference.html">Add ServiceStack Reference</a> clients. We&#39;re excited to announce even more new features for AutoQuery in this release - making it more capable and productive than ever!</p><p>AutoQuery Viewer is an exciting new feature providing an automatic UI to quickly browse and query all your AutoQuery Services!</p><p><a href="http://github.servicestack.net/ss_admin/autoquery" target="_blank" rel="noopener noreferrer"><img src="https://raw.githubusercontent.com/ServiceStack/Admin/master/img/query-default-values.png" alt=""></a></p><blockquote><p><a href="https://youtu.be/YejYkCvKsuQ" target="_blank" rel="noopener noreferrer">YouTube Demo</a></p></blockquote><p>AutoQuery Viewer is a React App that&#39;s bundled within a single <code>ServiceStack.Admin.dll</code> that&#39;s available from NuGet at:</p><h3 id="install-servicestack-admin" tabindex="-1">Install ServiceStack.Admin <a class="header-anchor" href="#install-servicestack-admin" aria-hidden="true">#</a></h3><pre><code>PM&gt; Install-Package ServiceStack.Admin
</code></pre><p>Signed Version also available from NuGet at <a href="http://nuget.org/packages/ServiceStack.Admin.Signed" target="_blank" rel="noopener noreferrer">ServiceStack.Admin.Signed</a></p><p>Then to add it to your project, just register the Plugin:</p><div class="language-csharp"><pre><code>Plugins<span class="token punctuation">.</span><span class="token function">Add</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">AdminFeature</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><p>Which requires <a href="/autoquery.html">AutoQuery</a>, if not already registered:</p><div class="language-csharp"><pre><code>Plugins<span class="token punctuation">.</span><span class="token function">Add</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">AutoQueryFeature</span> <span class="token punctuation">{</span> MaxLimit <span class="token operator">=</span> <span class="token number">100</span> <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><p>Once enabled a link to the AutoQuery Viewer will appear under <strong>Plugin Links</strong> in your <a href="/metadata-page.html">Metadata Page</a>:</p><p><img src="https://raw.githubusercontent.com/ServiceStack/Admin/master/img/metadata-plugin-link.png" alt=""></p><blockquote><p>Or you can navigate to it directly at <code>/ss_admin/</code></p></blockquote><p>As it&#39;s quick to add, we&#39;ve already enabled it in a number of existing Live Demo&#39;s containing AutoQuery Services:</p><h3 id="live-examples" tabindex="-1">Live Examples <a class="header-anchor" href="#live-examples" aria-hidden="true">#</a></h3><ul><li><a href="http://github.servicestack.net/ss_admin/" target="_blank" rel="noopener noreferrer">http://github.servicestack.net/ss_admin/</a></li><li><a href="http://northwind.netcore.io/ss_admin/" target="_blank" rel="noopener noreferrer">http://northwind.netcore.io/ss_admin/</a></li><li><a href="http://stackapis.netcore.io/ss_admin/" target="_blank" rel="noopener noreferrer">http://stackapis.netcore.io/ss_admin/</a></li><li><a href="https://techstacks.io/ss_admin/" target="_blank" rel="noopener noreferrer">https://techstacks.io/ss_admin/</a></li></ul><h3 id="default-minimal-ui" tabindex="-1">Default Minimal UI <a class="header-anchor" href="#default-minimal-ui" aria-hidden="true">#</a></h3><p>By default AutoQuery Services start with a minimal UI that uses the Request DTO name to identify the Query. An example of this can be seen with the <a href="http://northwind.netcore.io/ss_admin/autoquery/QueryCustomers" target="_blank" rel="noopener noreferrer">Northwind AutoQuery Services</a> below:</p><div class="language-csharp"><pre><code><span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">Route</span><span class="token attribute-arguments"><span class="token punctuation">(</span><span class="token string">&quot;/query/customers&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">QueryCustomers</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">QueryBase<span class="token punctuation">&lt;</span>Customer<span class="token punctuation">&gt;</span></span></span> <span class="token punctuation">{</span><span class="token punctuation">}</span>

<span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">Route</span><span class="token attribute-arguments"><span class="token punctuation">(</span><span class="token string">&quot;/query/orders&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">QueryOrders</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">QueryBase<span class="token punctuation">&lt;</span>Order<span class="token punctuation">&gt;</span></span></span> <span class="token punctuation">{</span><span class="token punctuation">}</span>
</code></pre></div><p>Which renders a UI with the default query and initial fields unpopulated:</p><p><a href="http://northwind.netcore.io/ss_admin/autoquery/QueryCustomers" target="_blank" rel="noopener noreferrer"><img src="https://raw.githubusercontent.com/ServiceStack/Admin/master/img/unannotated-autoquery-services.png" alt=""></a></p><h3 id="marking-up-autoquery-services" tabindex="-1">Marking up AutoQuery Services <a class="header-anchor" href="#marking-up-autoquery-services" aria-hidden="true">#</a></h3><p>To provide a more useful experience to end users you can also markup your AutoQuery Services by annotating them with the <code>[AutoQueryViewer]</code> attribute, as seen in <a href="http://github.servicestack.net/ss_admin/autoquery/QueryRepos" target="_blank" rel="noopener noreferrer">GitHub QueryRepos</a>:</p><div class="language-csharp"><pre><code><span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">Route</span><span class="token attribute-arguments"><span class="token punctuation">(</span><span class="token string">&quot;/repos&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">AutoQueryViewer</span><span class="token attribute-arguments"><span class="token punctuation">(</span>IconUrl <span class="token operator">=</span> <span class="token string">&quot;octicon:repo&quot;</span><span class="token punctuation">,</span>    
    Title <span class="token operator">=</span> <span class="token string">&quot;ServiceStack Repositories&quot;</span><span class="token punctuation">,</span> 
    Description <span class="token operator">=</span> <span class="token string">&quot;Browse different ServiceStack repos&quot;</span><span class="token punctuation">,</span>
    DefaultSearchField <span class="token operator">=</span> <span class="token string">&quot;Language&quot;</span><span class="token punctuation">,</span> DefaultSearchType <span class="token operator">=</span> <span class="token string">&quot;=&quot;</span><span class="token punctuation">,</span> DefaultSearchText <span class="token operator">=</span> <span class="token string">&quot;C#&quot;</span><span class="token punctuation">,</span>
    DefaultFields <span class="token operator">=</span> <span class="token string">&quot;Id,Name,Language,Description:500,Homepage,Has_Wiki&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">QueryRepos</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">QueryBase<span class="token punctuation">&lt;</span>GithubRepo<span class="token punctuation">&gt;</span></span></span> <span class="token punctuation">{</span><span class="token punctuation">}</span>
</code></pre></div><p>The additional metadata is then used to customize the UI at the following locations:</p><p><a href="http://github.servicestack.net/ss_admin/autoquery/QueryRepos" target="_blank" rel="noopener noreferrer"><img src="https://raw.githubusercontent.com/ServiceStack/Admin/master/img/query-default-values-markup.png" alt=""></a></p><p>Where <code>Title</code>, <code>Description</code>, <code>DefaultSearchField</code>, <code>DefaultSearchType</code> and <code>DefaultSearchText</code> is a straight forward placeholder replacement.</p><h4 id="iconurl" tabindex="-1">IconUrl <a class="header-anchor" href="#iconurl" aria-hidden="true">#</a></h4><p>Can either be an url to a <strong>24x24</strong> icon or preferably to avoid relying on any external resources, Admin UI embeds both <a href="https://design.google.com/icons/" target="_blank" rel="noopener noreferrer">Google&#39;s Material Design Icons</a> and <a href="https://octicons.github.com/" target="_blank" rel="noopener noreferrer">GitHub&#39;s Octicon</a> fonts which can be referenced using the custom <code>octicon:</code> and <code>material-icons:</code> schemes, e.g:</p><ul><li>octicon:icon</li><li>material-icons:cast</li></ul><h4 id="defaultfields" tabindex="-1">DefaultFields <a class="header-anchor" href="#defaultfields" aria-hidden="true">#</a></h4><p>Can hold a subset list of fields from the AutoQuery <strong>Response Type</strong> in the order you want them displayed. By default fields have a max-width of <strong>300px</strong> but we can override this default with a <code>:</code> suffix as seen with <code>Description:500</code> which changes the Description column width to <strong>500px</strong>. Any text longer than its width is automatically clipped, but you can still see the full-text by hovering over the field or by clicking the AutoQuery generated link, calling the AutoQuery Service and viewing the entire results.</p><blockquote><p>For more, see <a href="https://github.com/ServiceStack/Admin#advanced-customizations" target="_blank" rel="noopener noreferrer">Advanced Customizations</a></p></blockquote><h3 id="filter-autoquery-services" tabindex="-1">Filter AutoQuery Services <a class="header-anchor" href="#filter-autoquery-services" aria-hidden="true">#</a></h3><p>The filter textbox can be used to quickly find and browse to AutoQuery Services:</p><p><img src="https://raw.githubusercontent.com/ServiceStack/Admin/master/img/filter-autoquery-services.png" alt=""></p><h3 id="authorized-only-queries" tabindex="-1">Authorized Only Queries <a class="header-anchor" href="#authorized-only-queries" aria-hidden="true">#</a></h3><p>Users only see Queries they have access to, this lets you further tailor the UI for users by using the <code>[Authenticate]</code>, Required Role or Permission attributes to ensure different users only see relevant queries, e.g.</p><div class="language-csharp"><pre><code><span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">RequiredRole</span><span class="token attribute-arguments"><span class="token punctuation">(</span><span class="token string">&quot;Sales&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">QueryOrders</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">QueryBase<span class="token punctuation">&lt;</span>Order<span class="token punctuation">&gt;</span></span></span> <span class="token punctuation">{</span><span class="token punctuation">}</span>
</code></pre></div><p>Since the Auth attributes are Request Filter Attributes with a server dependency to <strong>ServiceStack.dll</strong>, in order to maintain and share a dependency-free <strong>ServiceModel.dll</strong> you should instead define a custom AutoQuery in your Service implementations which will inherit any Service or Action filter attributes as normal:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">QueryOrders</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">QueryBase<span class="token punctuation">&lt;</span>Order<span class="token punctuation">&gt;</span></span></span> <span class="token punctuation">{</span><span class="token punctuation">}</span>

<span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">RequiredRole</span><span class="token attribute-arguments"><span class="token punctuation">(</span><span class="token string">&quot;Sales&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">SalesServices</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">Service</span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name">IAutoQuery</span> AutoQuery <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>

    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">object</span></span> <span class="token function">Any</span><span class="token punctuation">(</span><span class="token class-name">QueryOrders</span> query<span class="token punctuation">)</span>
    <span class="token punctuation">{</span>
        <span class="token keyword">return</span> AutoQuery<span class="token punctuation">.</span><span class="token function">Execute</span><span class="token punctuation">(</span>query<span class="token punctuation">,</span> AutoQuery<span class="token punctuation">.</span><span class="token function">CreateQuery</span><span class="token punctuation">(</span>query<span class="token punctuation">,</span> Request<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><h3 id="updated-in-real-time" tabindex="-1">Updated in Real-time <a class="header-anchor" href="#updated-in-real-time" aria-hidden="true">#</a></h3><p>To enable a fast and productive UX, the generated AutoQuery link and query results are refreshed as-you-type, in addition any change to a any query immediately saves the App&#39;s state to <strong>localStorage</strong> so users queries are kept across page refreshes and browser restarts.</p><p><img src="https://raw.githubusercontent.com/ServiceStack/Admin/master/img/search-as-type.png" alt=""></p><p>The generated AutoQuery Url is kept in-sync and captures the state of the current query and serves as a good source for learning how to construct AutoQuery requests that can be used as-is in client applications.</p><h3 id="multiple-conditions" tabindex="-1">Multiple Conditions <a class="header-anchor" href="#multiple-conditions" aria-hidden="true">#</a></h3><p>Queries can be constructed with multiple conditions by hitting <strong>Enter</strong> or clicking on the <strong>green (+)</strong> button (activated when a condition is valid), adding it to the conditions list and clearing the search text:</p><p><img src="https://raw.githubusercontent.com/ServiceStack/Admin/master/img/multiple-conditions.png" alt=""></p><p>Clicking the <strong>red</strong> remove icon removes the condition.</p><h3 id="change-content-type" tabindex="-1">Change Content-Type <a class="header-anchor" href="#change-content-type" aria-hidden="true">#</a></h3><p>You can force a query to return a specific Content-Type response by clicking on one of the format links. E.g clicking on <strong>json</strong> link will add the <strong>.json</strong> extension to the generated url, overriding the browser&#39;s default Content-Type to specify a JSON response:</p><p><img src="https://raw.githubusercontent.com/ServiceStack/Admin/master/img/custom-content-types.png" alt=""></p><h3 id="customize-columns" tabindex="-1">Customize Columns <a class="header-anchor" href="#customize-columns" aria-hidden="true">#</a></h3><p>Results can further customized to show only the columns you&#39;re interested in by clicking on the <strong>show/hide columns</strong> icon and selecting the columns you want to see in the order you want them added:</p><p><img src="https://raw.githubusercontent.com/ServiceStack/Admin/master/img/customize-columns.png" alt=""></p><h3 id="sorting-columns-and-paging-results" tabindex="-1">Sorting Columns and Paging Results <a class="header-anchor" href="#sorting-columns-and-paging-results" aria-hidden="true">#</a></h3><p>Results can be sorted in descending or ascending order by clicking on the column headers:</p><p><img src="https://raw.githubusercontent.com/ServiceStack/Admin/master/img/paging-queries.png" alt=""></p><p>Clicking the back/forward navigation icons on the left will page through the results in the order specified.</p><h2 id="autoquery-enhancements" tabindex="-1">AutoQuery Enhancements <a class="header-anchor" href="#autoquery-enhancements" aria-hidden="true">#</a></h2><p>We&#39;ve also added a number of new features to AutoQuery that improves performance and enables greater flexibility for your AutoQuery Services:</p><h3 id="parameterized-autoquery" tabindex="-1">Parameterized AutoQuery <a class="header-anchor" href="#parameterized-autoquery" aria-hidden="true">#</a></h3><p>AutoQuery now generates parameterized sql for all queries where the <code>{Value}</code> placeholder in the AutoQuery Templates have been changed to use db parameters.</p><h3 id="customizable-fields" tabindex="-1">Customizable Fields <a class="header-anchor" href="#customizable-fields" aria-hidden="true">#</a></h3><p>You can now customize which fields you want returned using the new <code>Fields</code> property available on all AutoQuery Services, e.g:</p><pre><code>?Fields=Id,Name,Description,JoinTableId
</code></pre><p>The Fields still need to be defined on the Response DTO as this feature doesn&#39;t change the Response DTO Schema, only which fields are populated. This does change the underlying RDBMS SELECT that&#39;s executed, also benefiting from reduced bandwidth between your RDBMS and App Server.</p><p>A useful <a href="/customize-json-responses.html#customize-json-responses-on-the-fly">JSON customization</a> that you can add when specifying custom fields is <code>ExcludeDefaultValues</code>, e.g:</p><pre><code>/query?Fields=Id,Name,Description,JoinTableId&amp;jsconfig=ExcludeDefaultValues
</code></pre><p>Which will remove any value type fields with a <strong>default value</strong> from the JSON response, e.g:</p><ul><li><a href="http://github.servicestack.net/repos.json?fields=Name,Homepage,Language,Updated_At" target="_blank" rel="noopener noreferrer">github.servicestack.net/repos.json?fields=Name,Homepage,Language,Updated_At</a></li><li><a href="http://github.servicestack.net/repos.json?fields=Name,Homepage,Language,Updated_At&amp;jsconfig=ExcludeDefaultValues" target="_blank" rel="noopener noreferrer">github.servicestack.net/repos.json?fields=Name,Homepage,Language,Updated_At&amp;jsconfig=ExcludeDefaultValues</a></li></ul><h3 id="multiple-conditions-1" tabindex="-1">Multiple Conditions <a class="header-anchor" href="#multiple-conditions-1" aria-hidden="true">#</a></h3><p>Previously unsupported, AutoQuery now allows specifying multiple conditions with the same name, e.g:</p><pre><code>?DescriptionContains=Service&amp;DescriptionContains=Stack
</code></pre><h3 id="named-connection" tabindex="-1">Named Connection <a class="header-anchor" href="#named-connection" aria-hidden="true">#</a></h3><p>Related to our improved support for multi-tenancy applications, AutoQuery can easily be used to query any number of different databases registered in your AppHost.</p><p>In the example below we configure our main RDBMS to use SQL Server and register a <strong>Named Connection</strong> to point to a <strong>Reporting</strong> PostgreSQL RDBMS:</p><div class="language-csharp"><pre><code><span class="token class-name"><span class="token keyword">var</span></span> dbFactory <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">OrmLiteConnectionFactory</span><span class="token punctuation">(</span>connString<span class="token punctuation">,</span> SqlServer2012Dialect<span class="token punctuation">.</span>Provider<span class="token punctuation">)</span><span class="token punctuation">;</span>
container<span class="token punctuation">.</span><span class="token generic-method"><span class="token function">Register</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>IDbConnectionFactory<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span>dbFactory<span class="token punctuation">)</span><span class="token punctuation">;</span>

dbFactory<span class="token punctuation">.</span><span class="token function">RegisterConnection</span><span class="token punctuation">(</span><span class="token string">&quot;Reporting&quot;</span><span class="token punctuation">,</span> pgConnString<span class="token punctuation">,</span> PostgreSqlDialect<span class="token punctuation">.</span>Provider<span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><p>Any normal AutoQuery Services like <code>QueryOrders</code> will use the default SQL Server connection whilst <code>QuerySales</code> will execute its query on the PostgreSQL <code>Reporting</code> Database instead:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">QueryOrders</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">QueryBase<span class="token punctuation">&lt;</span>Order<span class="token punctuation">&gt;</span></span></span> <span class="token punctuation">{</span><span class="token punctuation">}</span>

<span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">NamedConnection</span><span class="token attribute-arguments"><span class="token punctuation">(</span><span class="token string">&quot;Reporting&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">QuerySales</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">QueryBase<span class="token punctuation">&lt;</span>Sales<span class="token punctuation">&gt;</span></span></span> <span class="token punctuation">{</span><span class="token punctuation">}</span>
</code></pre></div><h3 id="generate-autoquery-services-from-ormlite-t4-templates" tabindex="-1">Generate AutoQuery Services from OrmLite T4 Templates <a class="header-anchor" href="#generate-autoquery-services-from-ormlite-t4-templates" aria-hidden="true">#</a></h3><p><a href="https://forums.servicestack.net/t/t4-template-autoquery-feature/1911" target="_blank" rel="noopener noreferrer">Richard Safier</a> from the ServiceStack community has extended OrmLite&#39;s T4 Templates to include support for generating AutoQuery Services for each Table POCO model using the new opt-in <a href="https://github.com/ServiceStack/ServiceStack.OrmLite/blob/0e5c42b84a55e446c39ff3c4e01f361916e591b1/src/T4/OrmLite.Poco.tt#L13" target="_blank" rel="noopener noreferrer">CreateAutoQueryTypes</a> option whilst the new <a href="https://github.com/ServiceStack/ServiceStack.OrmLite/blob/0e5c42b84a55e446c39ff3c4e01f361916e591b1/src/T4/OrmLite.Poco.tt#L14" target="_blank" rel="noopener noreferrer">AddNamedConnection</a> option can be used to generate <code>[NamedConnection]</code> annotations.</p><p>With this feature Richard was able to generate thousands of fully-queryable AutoQuery Services spanning multiple databases in a single ServiceStack instance with just the T4 templates and configuration.</p><h3 id="adminfeature-rich-ui-implementation" tabindex="-1">AdminFeature Rich UI Implementation <a class="header-anchor" href="#adminfeature-rich-ui-implementation" aria-hidden="true">#</a></h3><p>We&#39;d like to make a special mention of how <code>AdminFeature</code> was built and deployed as ServiceStack makes it really easy to package and deploy rich plugins with complex UI and behavior encapsulated within a single plugin - which we hope spurs the creation of even richer community <a href="https://github.com/ServiceStack/ServiceStack/wiki/Plugins" target="_blank" rel="noopener noreferrer">Plugins</a>!</p><p>Development of <code>AdminFeature</code> is maintained in a TypeScript 1.8 + JSPM + React <a href="https://github.com/ServiceStack/Admin/tree/master/src/ServiceStack.Admin.WebHost" target="_blank" rel="noopener noreferrer">ServiceStack.Admin.WebHost</a> project where it&#39;s structured to provide an optimal iterative development experience. To re-package the App we just call on JSPM to create our app.js bundle by pointing it to the React App&#39;s <code>main</code> entry point:</p><pre><code>jspm bundle -m src\\main ..\\ServiceStack.Admin\\ss_admin\\app.js
</code></pre><p>Then each of the static resources are copied into the Plugins <a href="https://github.com/ServiceStack/Admin/tree/master/src/ServiceStack.Admin" target="_blank" rel="noopener noreferrer">ServiceStack.Admin</a> project with their <strong>Build Action</strong> set to <strong>Embedded Resource</strong> so they&#39;re embedded in the <strong>ServiceStack.Admin.dll</strong>.</p><p>To add the Embedded Resources to the <a href="/virtual-file-system.html">Virtual File System</a> the <code>AdminFeature</code> just adds it to <code>Config.EmbeddedResourceBaseTypes</code> (also making it safe to ILMerge).</p><p>The entire server implementation for the <code>AdminFeature</code> is contained below, most of which is dedicated to supporting when ServiceStack is mounted at both root <code>/</code> or a custom path (e.g. <code>/api</code>) - which it supports by rewriting the embedded <code>index.html</code> with the <code>HandlerFactoryPath</code> before returning it:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">AdminFeature</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">IPlugin</span><span class="token punctuation">,</span> <span class="token class-name">IPreInitPlugin</span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">void</span></span> <span class="token function">Configure</span><span class="token punctuation">(</span><span class="token class-name">IAppHost</span> appHost<span class="token punctuation">)</span>
    <span class="token punctuation">{</span>
        <span class="token comment">//Register ServiceStack.Admin.dll as an Embedded Resource to VirtualFiles</span>
        appHost<span class="token punctuation">.</span>Config<span class="token punctuation">.</span>EmbeddedResourceBaseTypes<span class="token punctuation">.</span><span class="token function">Add</span><span class="token punctuation">(</span><span class="token keyword">typeof</span><span class="token punctuation">(</span><span class="token type-expression class-name">AdminFeature</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>

    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">void</span></span> <span class="token function">Register</span><span class="token punctuation">(</span><span class="token class-name">IAppHost</span> appHost<span class="token punctuation">)</span>
    <span class="token punctuation">{</span>
        <span class="token class-name"><span class="token keyword">var</span></span> indexHtml <span class="token operator">=</span> appHost<span class="token punctuation">.</span>VirtualFileSources<span class="token punctuation">.</span><span class="token function">GetFile</span><span class="token punctuation">(</span><span class="token string">&quot;ss_admin/index.html&quot;</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">ReadAllText</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token keyword">if</span> <span class="token punctuation">(</span>appHost<span class="token punctuation">.</span>Config<span class="token punctuation">.</span>HandlerFactoryPath <span class="token operator">!=</span> <span class="token keyword">null</span><span class="token punctuation">)</span> <span class="token comment">//Inject HandlerFactoryPath if mounted at /custom path</span>
            indexHtml <span class="token operator">=</span> indexHtml<span class="token punctuation">.</span><span class="token function">Replace</span><span class="token punctuation">(</span><span class="token string">&quot;/ss_admin&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;/{0}/ss_admin&quot;</span><span class="token punctuation">.</span><span class="token function">Fmt</span><span class="token punctuation">(</span>appHost<span class="token punctuation">.</span>Config<span class="token punctuation">.</span>HandlerFactoryPath<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

        appHost<span class="token punctuation">.</span>CatchAllHandlers<span class="token punctuation">.</span><span class="token function">Add</span><span class="token punctuation">(</span><span class="token punctuation">(</span>httpMethod<span class="token punctuation">,</span> pathInfo<span class="token punctuation">,</span> filePath<span class="token punctuation">)</span> <span class="token operator">=&gt;</span> 
            pathInfo<span class="token punctuation">.</span><span class="token function">StartsWith</span><span class="token punctuation">(</span><span class="token string">&quot;/ss_admin&quot;</span><span class="token punctuation">)</span> 
                <span class="token punctuation">?</span> <span class="token punctuation">(</span>pathInfo <span class="token operator">==</span> <span class="token string">&quot;/ss_admin/index.html&quot;</span> <span class="token operator">||</span> <span class="token operator">!</span>appHost<span class="token punctuation">.</span>VirtualFileSources<span class="token punctuation">.</span><span class="token function">FileExists</span><span class="token punctuation">(</span>pathInfo<span class="token punctuation">)</span>
                    <span class="token punctuation">?</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">StaticContentHandler</span><span class="token punctuation">(</span>indexHtml<span class="token punctuation">,</span> MimeTypes<span class="token punctuation">.</span>Html<span class="token punctuation">)</span> <span class="token keyword">as</span> <span class="token class-name">IHttpHandler</span>
                    <span class="token punctuation">:</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">StaticFileHandler</span><span class="token punctuation">(</span>appHost<span class="token punctuation">.</span>VirtualFileSources<span class="token punctuation">.</span><span class="token function">GetFile</span><span class="token punctuation">(</span>pathInfo<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
                <span class="token punctuation">:</span> <span class="token keyword">null</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

        appHost<span class="token punctuation">.</span><span class="token generic-method"><span class="token function">GetPlugin</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>MetadataFeature<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token punctuation">)</span>
            <span class="token punctuation">.</span><span class="token function">AddPluginLink</span><span class="token punctuation">(</span><span class="token string">&quot;/ss_admin/autoquery/&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;AutoQuery Viewer&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">//Add link to /metadata page</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>To power most of its UI, AutoQuery Viewer makes use of the <a href="https://github.com/ServiceStack/Admin#advanced-customizations" target="_blank" rel="noopener noreferrer">existing Metadata service in AutoQuery</a>.</p><h4 id="code-first-poco-simplicity" tabindex="-1">Code-first POCO Simplicity <a class="header-anchor" href="#code-first-poco-simplicity" aria-hidden="true">#</a></h4><p>Other classes worth reviewing is the <a href="https://github.com/ServiceStack/Admin/blob/master/tests/Admin.Tasks/GitHubTasks.cs" target="_blank" rel="noopener noreferrer">GitHubTasks.cs</a> and <a href="https://github.com/ServiceStack/Admin/blob/master/tests/Admin.Tasks/StackOverflowTasks.cs" target="_blank" rel="noopener noreferrer">StackOverflowTasks.cs</a> containing the NUnit tests used to create the test sqlite database on-the-fly, directly from the GitHub and StackOverflow JSON APIs, the ease of which speaks to the simplicity of <a href="http://stackoverflow.com/a/32940275/85785" target="_blank" rel="noopener noreferrer">ServiceStack&#39;s code-first POCO approach</a>.</p><h2 id="server-events" tabindex="-1"><a href="/server-events.html">Server Events</a> <a class="header-anchor" href="#server-events" aria-hidden="true">#</a></h2><p>We&#39;ve published a couple of new examples projects showing how easy it is to create rich, interactive native mobile and web apps using Server Events.</p><h3 id="xamarin-android-chat" tabindex="-1"><a href="https://github.com/ServiceStackApps/AndroidXamarinChat" target="_blank" rel="noopener noreferrer">Xamarin.Android Chat</a> <a class="header-anchor" href="#xamarin-android-chat" aria-hidden="true">#</a></h3><p>Xamarin.Android Chat utilizes the <a href="/csharp-server-events-client.html">.NET PCL Server Events Client</a> to create an Android Chat App connecting to the existing <a href="http://chat.netcore.io/" target="_blank" rel="noopener noreferrer">chat.netcore.io</a> back-end where it&#39;s able to communicate with existing Ajax clients and other connected Android Chat Apps. The example shows how to enable a native integrated experience by translating the existing <code>cmd.announce</code> message into an Android notification as well shows how to use Xamarin.Auth to authenticate with ServiceStack using Twitter Auth.</p><p>Click the video below to see a quick demo of it in action:</p><blockquote><p><a href="https://www.youtube.com/watch?v=tImAm2LURu0" target="_blank" rel="noopener noreferrer">YouTube Video</a></p></blockquote><p><a href="https://www.youtube.com/watch?v=tImAm2LURu0" target="_blank" rel="noopener noreferrer"><img src="https://raw.githubusercontent.com/ServiceStack/Assets/master/img/livedemos/xamarin-android-server-events.png" alt=""></a></p><p>For a deeper dive, checkout the feature list and source code from the <a href="https://github.com/ServiceStackApps/AndroidXamarinChat" target="_blank" rel="noopener noreferrer">AndroidXamarinChat</a> GitHub repo.</p><h3 id="networked-time-traveller-shape-creator" tabindex="-1"><a href="https://github.com/ServiceStackApps/typescript-redux#example-9---real-time-networked-time-traveller" target="_blank" rel="noopener noreferrer">Networked Time Traveller Shape Creator</a> <a class="header-anchor" href="#networked-time-traveller-shape-creator" aria-hidden="true">#</a></h3><p>We&#39;ve also added Server Events to convert a <a href="https://github.com/ServiceStackApps/typescript-redux#example-8---time-travelling-using-state-snapshots" target="_blank" rel="noopener noreferrer">stand-alone Time Traveller Shape Creator</a> into a networked one where users can <strong>connect to</strong> and <strong>watch</strong> other users using the App in real-time similar to how users can use Remote Desktop to watch another computer&#39;s screen:</p><p><a href="http://redux.servicestack.net" target="_blank" rel="noopener noreferrer"><img src="https://raw.githubusercontent.com/ServiceStack/Assets/master/img/livedemos/redux-chrome-safari.png" alt=""></a></p><blockquote><p>Live demo at: <a href="http://redux.servicestack.net" target="_blank" rel="noopener noreferrer">http://redux.servicestack.net</a></p></blockquote><p>Surprisingly most of the client code required to enable this is encapsulated within a single <a href="https://github.com/ServiceStackApps/typescript-redux/blob/master/src/TypeScriptRedux/src/example09/Connect.tsx" target="_blank" rel="noopener noreferrer">React Connect component</a>.</p><p>The networked Shape Creator makes use of 2 back-end Services that lets users publish their actions to a channel and another Service to send a direct message to a User. The <a href="https://github.com/ServiceStackApps/typescript-redux/blob/master/src/TypeScriptRedux/Global.asax.cs" target="_blank" rel="noopener noreferrer">implementation for both services</a> is contained below:</p><div class="language-csharp"><pre><code><span class="token comment">//Services Contract</span>
<span class="token punctuation">[</span><span class="token function">Route</span><span class="token punctuation">(</span><span class="token string">&quot;/publish-channel/{Channel}&quot;</span><span class="token punctuation">)</span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">PublishToChannel</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">IReturnVoid</span><span class="token punctuation">,</span> <span class="token class-name">IRequiresRequestStream</span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span></span> Channel <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span></span> Selector <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token keyword">public</span> <span class="token return-type class-name">Stream</span> RequestStream <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">Route</span><span class="token attribute-arguments"><span class="token punctuation">(</span><span class="token string">&quot;/send-user/{To}&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">SendUser</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">IReturnVoid</span><span class="token punctuation">,</span> <span class="token class-name">IRequiresRequestStream</span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span></span> To <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span></span> Selector <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token keyword">public</span> <span class="token return-type class-name">Stream</span> RequestStream <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token comment">//Services Implementation</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">ReduxServices</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">Service</span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name">IServerEvents</span> ServerEvents <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>

    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">void</span></span> <span class="token function">Any</span><span class="token punctuation">(</span><span class="token class-name">PublishToChannel</span> request<span class="token punctuation">)</span>
    <span class="token punctuation">{</span>
        <span class="token class-name"><span class="token keyword">var</span></span> msg <span class="token operator">=</span> request<span class="token punctuation">.</span>RequestStream<span class="token punctuation">.</span><span class="token function">ReadFully</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">FromUtf8Bytes</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        ServerEvents<span class="token punctuation">.</span><span class="token function">NotifyChannel</span><span class="token punctuation">(</span>request<span class="token punctuation">.</span>Channel<span class="token punctuation">,</span> request<span class="token punctuation">.</span>Selector<span class="token punctuation">,</span> msg<span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>

    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">void</span></span> <span class="token function">Any</span><span class="token punctuation">(</span><span class="token class-name">SendUser</span> request<span class="token punctuation">)</span>
    <span class="token punctuation">{</span>
        <span class="token class-name"><span class="token keyword">var</span></span> msg <span class="token operator">=</span> request<span class="token punctuation">.</span>RequestStream<span class="token punctuation">.</span><span class="token function">ReadFully</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">FromUtf8Bytes</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        ServerEvents<span class="token punctuation">.</span><span class="token function">NotifyUserId</span><span class="token punctuation">(</span>request<span class="token punctuation">.</span>To<span class="token punctuation">,</span> request<span class="token punctuation">.</span>Selector<span class="token punctuation">,</span> msg<span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>Essentially just calling <code>IServerEvents</code> to forward the raw JSON Request Body to the specified channel or user.</p><h3 id="updating-channels-on-live-subscriptions" tabindex="-1">Updating Channels on Live Subscriptions <a class="header-anchor" href="#updating-channels-on-live-subscriptions" aria-hidden="true">#</a></h3><p>Previously to change Server Event channel subscriptions you would need to create a new connection with the channels you wanted to join. You can now update a live Server Events connection with Channels you want to Join or Leave using the new built-in ServerEvents <code>UpdateEventSubscriber</code> Service:</p><div class="language-csharp"><pre><code><span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">Route</span><span class="token attribute-arguments"><span class="token punctuation">(</span><span class="token string">&quot;/event-subscribers/{Id}&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;POST&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">UpdateEventSubscriber</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">IReturn<span class="token punctuation">&lt;</span>UpdateEventSubscriberResponse<span class="token punctuation">&gt;</span></span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span></span> Id <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span><span class="token punctuation">[</span><span class="token punctuation">]</span></span> SubscribeChannels <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span><span class="token punctuation">[</span><span class="token punctuation">]</span></span> UnsubscribeChannels <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>This lets you modify your active subscription with channels you want to join or leave with a HTTP POST Request, e.g:</p><pre><code>POST /event-subscribers/{subId}
SubscribeChannels=chan1,chan2&amp;UnsubscribeChannels=chan3,chan4
</code></pre><h3 id="new-onupdate-notification" tabindex="-1">New onUpdate Notification <a class="header-anchor" href="#new-onupdate-notification" aria-hidden="true">#</a></h3><p>As this modifies the active subscription it also publishes a new <strong>onUpdate</strong> notification to all channel subscribers so they&#39;re able to maintain up-to-date info on each subscriber.</p><p>In C# <code>ServerEventsClient</code> this can be handled together with <strong>onJoin</strong> and <strong>onLeave</strong> events using <code>OnCommand</code>:</p><div class="language-csharp"><pre><code>client<span class="token punctuation">.</span>OnCommand <span class="token operator">=</span> msg <span class="token operator">=&gt;</span> <span class="token range operator">..</span><span class="token punctuation">.</span><span class="token punctuation">;</span> <span class="token comment">//= ServerEventJoin, ServerEventLeave or ServerEventUpdate</span>
</code></pre></div><p>In the ss-utils JavaScript Client this can be handled with a Global Event Handler, e.g:</p><div class="language-javascript"><pre><code><span class="token function">$</span><span class="token punctuation">(</span>source<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">handleServerEvents</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
    handlers<span class="token operator">:</span> <span class="token punctuation">{</span>
        <span class="token function-variable function">onConnect</span><span class="token operator">:</span> <span class="token parameter">connectedUserInfo</span> <span class="token operator">=&gt;</span> <span class="token punctuation">{</span> <span class="token operator">...</span> <span class="token punctuation">}</span><span class="token punctuation">,</span>
        <span class="token function-variable function">onJoin</span><span class="token operator">:</span> <span class="token parameter">userInfo</span> <span class="token operator">=&gt;</span> <span class="token punctuation">{</span> <span class="token operator">...</span> <span class="token punctuation">}</span><span class="token punctuation">,</span>
        <span class="token function-variable function">onLeave</span><span class="token operator">:</span> <span class="token parameter">userInfo</span> <span class="token operator">=&gt;</span> <span class="token punctuation">{</span> <span class="token operator">...</span> <span class="token punctuation">}</span><span class="token punctuation">,</span>
        <span class="token function-variable function">onUpdate</span><span class="token operator">:</span> <span class="token parameter">userInfo</span> <span class="token operator">=&gt;</span> <span class="token punctuation">{</span> <span class="token operator">...</span> <span class="token punctuation">}</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><h3 id="net-updatesubscriber-apis" tabindex="-1">.NET UpdateSubscriber APIs <a class="header-anchor" href="#net-updatesubscriber-apis" aria-hidden="true">#</a></h3><p>Typed versions of this API is built into the C# <code>ServerEventsClient</code> in both sync/async versions:</p><div class="language-csharp"><pre><code>client<span class="token punctuation">.</span><span class="token function">UpdateSubscriber</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">UpdateEventSubscriber</span> <span class="token punctuation">{</span> 
    SubscribeChannels <span class="token operator">=</span> <span class="token keyword">new</span><span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">{</span> <span class="token string">&quot;chan1&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;chan2&quot;</span> <span class="token punctuation">}</span><span class="token punctuation">,</span>
    UnsubscribeChannels <span class="token operator">=</span> <span class="token keyword">new</span><span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">{</span> <span class="token string">&quot;chan3&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;chan4&quot;</span> <span class="token punctuation">}</span><span class="token punctuation">,</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

client<span class="token punctuation">.</span><span class="token function">SubscribeToChannels</span><span class="token punctuation">(</span><span class="token string">&quot;chan1&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;chan2&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
client<span class="token punctuation">.</span><span class="token function">UnsubscribeFromChannels</span><span class="token punctuation">(</span><span class="token string">&quot;chan3&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;chan4&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token keyword">await</span> client<span class="token punctuation">.</span><span class="token function">SubscribeToChannelsAsync</span><span class="token punctuation">(</span><span class="token string">&quot;chan1&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;chan2&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token keyword">await</span> client<span class="token punctuation">.</span><span class="token function">UnsubscribeFromChannelsAsync</span><span class="token punctuation">(</span><span class="token string">&quot;chan3&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;chan4&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><h3 id="javascript-updatesubscriber-apis" tabindex="-1">JavaScript UpdateSubscriber APIs <a class="header-anchor" href="#javascript-updatesubscriber-apis" aria-hidden="true">#</a></h3><p>As well as in ServiceStack&#39;s ss-utils JavaScript library:</p><div class="language-javascript"><pre><code>$<span class="token punctuation">.</span>ss<span class="token punctuation">.</span><span class="token function">updateSubscriber</span><span class="token punctuation">(</span><span class="token punctuation">{</span> 
    SubscribeChannels<span class="token operator">:</span> <span class="token string">&quot;chan1,chan2&quot;</span><span class="token punctuation">,</span>
    UnsubscribeChannels<span class="token operator">:</span> <span class="token string">&quot;chan3,chan4&quot;</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

$<span class="token punctuation">.</span>ss<span class="token punctuation">.</span><span class="token function">subscribeToChannels</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token string">&quot;chan1&quot;</span><span class="token punctuation">,</span><span class="token string">&quot;chan2&quot;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token parameter">response</span> <span class="token operator">=&gt;</span> <span class="token operator">...</span><span class="token punctuation">,</span> <span class="token parameter">error</span> <span class="token operator">=&gt;</span> <span class="token operator">...</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
$<span class="token punctuation">.</span>ss<span class="token punctuation">.</span><span class="token function">unsubscribeFromChannels</span><span class="token punctuation">(</span><span class="token punctuation">[</span><span class="token string">&quot;chan3&quot;</span><span class="token punctuation">,</span><span class="token string">&quot;chan4&quot;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token parameter">response</span> <span class="token operator">=&gt;</span> <span class="token operator">...</span><span class="token punctuation">,</span> <span class="token parameter">error</span> <span class="token operator">=&gt;</span> <span class="token operator">...</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><h3 id="serverevents-update-channel-apis" tabindex="-1">ServerEvents Update Channel APIs <a class="header-anchor" href="#serverevents-update-channel-apis" aria-hidden="true">#</a></h3><p>Whilst internally, from within ServiceStack you can update a channel&#39;s subscription using the new <a href="https://github.com/ServiceStack/ServiceStack/blob/b9a33c34d0b0eedbcc6b3483257f1dc37bbf713f/src/ServiceStack/ServerEventsFeature.cs#L1004" target="_blank" rel="noopener noreferrer">IServerEvents</a> APIs:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">interface</span> <span class="token class-name">IServerEvents</span> 
<span class="token punctuation">{</span>
    <span class="token range operator">..</span><span class="token punctuation">.</span>
    <span class="token return-type class-name"><span class="token keyword">void</span></span> <span class="token function">SubscribeToChannels</span><span class="token punctuation">(</span><span class="token class-name"><span class="token keyword">string</span></span> subscriptionId<span class="token punctuation">,</span> <span class="token class-name"><span class="token keyword">string</span><span class="token punctuation">[</span><span class="token punctuation">]</span></span> channels<span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token return-type class-name"><span class="token keyword">void</span></span> <span class="token function">UnsubscribeFromChannels</span><span class="token punctuation">(</span><span class="token class-name"><span class="token keyword">string</span></span> subscriptionId<span class="token punctuation">,</span> <span class="token class-name"><span class="token keyword">string</span><span class="token punctuation">[</span><span class="token punctuation">]</span></span> channels<span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre></div><h2 id="typescript-react-app-beta" tabindex="-1"><a href="https://github.com/ServiceStackApps/typescript-react-template/" target="_blank" rel="noopener noreferrer">TypeScript React App (beta)</a> <a class="header-anchor" href="#typescript-react-app-beta" aria-hidden="true">#</a></h2><p><a href="https://github.com/ServiceStackApps/typescript-react-template/" target="_blank" rel="noopener noreferrer"><img src="https://raw.githubusercontent.com/ServiceStack/Assets/master/img/release-notes/typescript-react-jspm-banner.png" alt=""></a></p><p>We&#39;ve spent a fair amount of time researching the JavaScript ecosystem to discover what we believe offers <a href="http://VS.NET" target="_blank" rel="noopener noreferrer">VS.NET</a> developers the most optimal balance of power, simplicity and tooling to build and maintain large JavaScript Apps. <a href="https://github.com/lukehoban/es6features" target="_blank" rel="noopener noreferrer">ES6</a> offers a number of language improvements to ES5-compatible JavaScript making it much more enjoyable to develop modern applications with, which we believe justifies the additional tooling needed to transpile it to support down-level ES5 browsers. Given the lack of support for Babel/ES6 in <a href="http://VS.NET" target="_blank" rel="noopener noreferrer">VS.NET</a>, the best option to access ES6 features is to use <a href="http://www.typescriptlang.org/" target="_blank" rel="noopener noreferrer">TypeScript</a> which also offers its own benefits over and beyond ES6.</p><p>The decision to use TypeScript also meant revisiting other tools used in our Single Page App templates. One of the most productive features in ES6/TypeScript is being able to easily use modules to modularize your code which provides an optimal development experience for maintaining large and complex code-bases. For this to work seamlessly we needed to integrate TypeScript modules with our front-end JavaScript package manager which is why we&#39;ve replaced <a href="http://bower.io/" target="_blank" rel="noopener noreferrer">bower</a> with <a href="http://jspm.io/" target="_blank" rel="noopener noreferrer">JSPM</a> and configured TypeScript to use the Universal <a href="https://github.com/systemjs/systemjs" target="_blank" rel="noopener noreferrer">SystemJS module format</a>.</p><p>Finally to minimize JavaScript fatigue, we&#39;ve removed as much complexity and moving parts as we could and have removed <a href="http://gruntjs.com/" target="_blank" rel="noopener noreferrer">Grunt</a> in favor of leaving only a <a href="http://gulpjs.com/" target="_blank" rel="noopener noreferrer">Gulp</a> JS build system without any <a href="https://github.com/ServiceStackApps/ReactDesktopApps#defaultapp-project" target="_blank" rel="noopener noreferrer">loss of functionality</a>.</p><p>With these changes we&#39;ve hand picked what we believe is the current <strong>Gold Standard</strong> for developing modern JavaScript Apps in <a href="http://VS.NET" target="_blank" rel="noopener noreferrer">VS.NET</a> with the just released <a href="http://www.typescriptlang.org/" target="_blank" rel="noopener noreferrer">TypeScript 1.8</a>, <a href="https://facebook.github.io/react/" target="_blank" rel="noopener noreferrer">React</a>, <a href="http://jspm.io/" target="_blank" rel="noopener noreferrer">JSPM</a>, <a href="http://gulpjs.com/" target="_blank" rel="noopener noreferrer">Gulp</a> and <a href="https://github.com/typings/typings" target="_blank" rel="noopener noreferrer">typings</a> (the successor to <a href="https://github.com/DefinitelyTyped/tsd" target="_blank" rel="noopener noreferrer">TSD</a>). We&#39;re also greatly benefiting from this Technology Stack ourselves with the development our latest <a href="https://github.com/ServiceStack/Admin" target="_blank" rel="noopener noreferrer">AutoQuery Viewer</a> TypeScript App.</p><p>We&#39;ve integrated these powerful combinations of technologies and packaged it in the new <strong>TypeScript React App (Beta)</strong> <a href="http://VS.NET" target="_blank" rel="noopener noreferrer">VS.NET</a> template that&#39;s now available in the updated <a href="https://github.com/ServiceStack/ServiceStackVS" target="_blank" rel="noopener noreferrer">ServiceStackVS VS.NET Extension</a>:</p><p><img src="https://raw.githubusercontent.com/ServiceStack/Assets/master/img/release-notes/typescript-react-jspm-template.png" alt=""></p><p>To learn more about this template and explore its different features. please see the <a href="https://github.com/ServiceStackApps/typescript-react-template/" target="_blank" rel="noopener noreferrer">in-depth typescript-react-template guide</a>.</p><h2 id="typescript-redux" tabindex="-1"><a href="https://github.com/ServiceStackApps/typescript-redux" target="_blank" rel="noopener noreferrer">TypeScript Redux</a> <a class="header-anchor" href="#typescript-redux" aria-hidden="true">#</a></h2><p>To help developers familiarize themselves with these technologies we&#39;ve also published an in-depth step-by-step guide for beginners that starts off building the simplest HelloWorld TypeScript React App from scratch then slowly growing with each example explaining how TypeScript, React and Redux can be used to easily create a more complex networked Time Travelling Shape Creator as seen in the final Example:</p><p><a href="https://github.com/ServiceStackApps/typescript-redux" target="_blank" rel="noopener noreferrer"><img src="https://raw.githubusercontent.com/ServiceStackApps/typescript-redux/master/img/preview-09.png" alt=""></a></p><blockquote><p>Live Demo: <a href="http://redux.servicestack.net" target="_blank" rel="noopener noreferrer">http://redux.servicestack.net</a></p></blockquote><p>Except for the final demo above, all other examples are pure client-side only demos, i.e. without any server dependencies and can be previewed directly from the static GitHub website below:</p><ul><li><a href="http://servicestackapps.github.io/typescript-redux/example01/" target="_blank" rel="noopener noreferrer">Example 1 - HelloWorld</a></li><li><a href="http://servicestackapps.github.io/typescript-redux/example02/" target="_blank" rel="noopener noreferrer">Example 2 - Modularizing HelloWorld</a></li><li><a href="http://servicestackapps.github.io/typescript-redux/example03/" target="_blank" rel="noopener noreferrer">Example 3 - Creating a stateful Component</a></li><li><a href="http://servicestackapps.github.io/typescript-redux/example04/" target="_blank" rel="noopener noreferrer">Example 4 - Change Counter to use Redux</a></li><li><a href="http://servicestackapps.github.io/typescript-redux/example05/" target="_blank" rel="noopener noreferrer">Example 5 - Use Provider to inject store in child Context</a></li><li><a href="http://servicestackapps.github.io/typescript-redux/example06/" target="_blank" rel="noopener noreferrer">Example 6 - Use connect() to make Components stateless</a></li><li><a href="http://servicestackapps.github.io/typescript-redux/example07/" target="_blank" rel="noopener noreferrer">Example 7 - Shape Creator</a></li><li><a href="http://servicestackapps.github.io/typescript-redux/example08/" target="_blank" rel="noopener noreferrer">Example 8 - Time Travelling using State Snapshots</a></li></ul><h2 id="ss-utils" tabindex="-1"><a href="/ss-utils-js.html">ss-utils</a> <a class="header-anchor" href="#ss-utils" aria-hidden="true">#</a></h2><h3 id="ss-utils-now-available-on-npm-and-definitelytyped" tabindex="-1">ss-utils now available on npm and DefinitelyTyped <a class="header-anchor" href="#ss-utils-now-available-on-npm-and-definitelytyped" aria-hidden="true">#</a></h3><p>To make it easier to develop with <strong>ss-utils</strong> in any of the npm-based Single Page Apps templates we&#39;re maintaining a copy of <a href="https://www.npmjs.com/package/ss-utils" target="_blank" rel="noopener noreferrer">ss-utils in npm</a> and have also added it to JSPM and DefinitelyTyped registry so you can now add it to your project like any other external dependency using JSPM:</p><pre><code>C:\\&gt; jspm install ss-utils
</code></pre><p>If you&#39;re using TypeScript, you can also download the accompanying TypeScript definition from:</p><pre><code>C:\\&gt;typings install ss-utils --ambient --save
</code></pre><p>Or if you&#39;re using the older tsd package manager: <code>tsd install ss-utils --save</code>.</p><h3 id="new-ss-utils-api-s" tabindex="-1">New ss-utils API&#39;s <a class="header-anchor" href="#new-ss-utils-api-s" aria-hidden="true">#</a></h3><p>We&#39;ve added new core utils to make it easier to create paths, urls, normalize JSON responses and send POST JSON Requests:</p><h4 id="combinepaths-and-createurl" tabindex="-1">combinePaths and createUrl <a class="header-anchor" href="#combinepaths-and-createurl" aria-hidden="true">#</a></h4><p>The new <code>combinePaths</code> and <code>createUrl</code> API&#39;s help with constructing urls, e.g:</p><div class="language-javascript"><pre><code>$<span class="token punctuation">.</span>ss<span class="token punctuation">.</span><span class="token function">combinePaths</span><span class="token punctuation">(</span><span class="token string">&quot;path&quot;</span><span class="token punctuation">,</span><span class="token string">&quot;to&quot;</span><span class="token punctuation">,</span><span class="token string">&quot;..&quot;</span><span class="token punctuation">,</span><span class="token string">&quot;join&quot;</span><span class="token punctuation">)</span>   <span class="token comment">//= path/join</span>
$<span class="token punctuation">.</span>ss<span class="token punctuation">.</span><span class="token function">createPath</span><span class="token punctuation">(</span><span class="token string">&quot;path/{foo}&quot;</span><span class="token punctuation">,</span> <span class="token punctuation">{</span>foo<span class="token operator">:</span><span class="token number">1</span><span class="token punctuation">,</span>bar<span class="token operator">:</span><span class="token number">2</span><span class="token punctuation">}</span><span class="token punctuation">)</span> <span class="token comment">//= path/1</span>

$<span class="token punctuation">.</span>ss<span class="token punctuation">.</span><span class="token function">createUrl</span><span class="token punctuation">(</span><span class="token string">&quot;http://host/path/{foo}&quot;</span><span class="token punctuation">,</span><span class="token punctuation">{</span>foo<span class="token operator">:</span><span class="token number">1</span><span class="token punctuation">,</span>bar<span class="token operator">:</span><span class="token number">2</span><span class="token punctuation">}</span><span class="token punctuation">)</span> <span class="token comment">//= http://host/path/1?bar=2</span>
</code></pre></div><p>This is a change from previous release where <code>createUrl()</code> behaved like <code>createPath()</code>.</p><h4 id="normalize-and-normalizekey" tabindex="-1">normalize and normalizeKey <a class="header-anchor" href="#normalize-and-normalizekey" aria-hidden="true">#</a></h4><p>The new <code>normalizeKey</code> and <code>normalize</code> APIs helps with normalizing JSON responses with different naming conventions by converting each property into lowercase with any <code>_</code> separators removed - <code>normalizeKey()</code> converts a single string whilst <code>normalize()</code> converts an entire object graph, e.g:</p><div class="language-javascript"><pre><code>$<span class="token punctuation">.</span>ss<span class="token punctuation">.</span><span class="token function">normalizeKey</span><span class="token punctuation">(</span><span class="token string">&quot;THE_KEY&quot;</span><span class="token punctuation">)</span> <span class="token comment">//= thekey</span>

<span class="token constant">JSON</span><span class="token punctuation">.</span><span class="token function">stringify</span><span class="token punctuation">(</span>
    $<span class="token punctuation">.</span>ss<span class="token punctuation">.</span><span class="token function">normalize</span><span class="token punctuation">(</span><span class="token punctuation">{</span><span class="token constant">THE_KEY</span><span class="token operator">:</span><span class="token string">&quot;key&quot;</span><span class="token punctuation">,</span>Foo<span class="token operator">:</span><span class="token string">&quot;foo&quot;</span><span class="token punctuation">,</span>bar<span class="token operator">:</span><span class="token punctuation">{</span><span class="token constant">A</span><span class="token operator">:</span><span class="token number">1</span><span class="token punctuation">}</span><span class="token punctuation">}</span><span class="token punctuation">)</span>
<span class="token punctuation">)</span>   <span class="token comment">//= {&quot;thekey&quot;:&quot;key&quot;,&quot;foo&quot;:&quot;foo&quot;,&quot;bar&quot;:{&quot;A&quot;:1}}</span>

<span class="token keyword">const</span> deep <span class="token operator">=</span> <span class="token boolean">true</span><span class="token punctuation">;</span>
<span class="token constant">JSON</span><span class="token punctuation">.</span><span class="token function">stringify</span><span class="token punctuation">(</span>
    $<span class="token punctuation">.</span>ss<span class="token punctuation">.</span><span class="token function">normalize</span><span class="token punctuation">(</span><span class="token punctuation">{</span><span class="token constant">THE_KEY</span><span class="token operator">:</span><span class="token string">&quot;key&quot;</span><span class="token punctuation">,</span>Foo<span class="token operator">:</span><span class="token string">&quot;foo&quot;</span><span class="token punctuation">,</span>bar<span class="token operator">:</span><span class="token punctuation">{</span><span class="token constant">A</span><span class="token operator">:</span><span class="token number">1</span><span class="token punctuation">}</span><span class="token punctuation">}</span><span class="token punctuation">,</span> deep<span class="token punctuation">)</span> 
<span class="token punctuation">)</span>   <span class="token comment">//= {&quot;thekey&quot;:&quot;key&quot;,&quot;foo&quot;:&quot;foo&quot;,&quot;bar&quot;:{&quot;a&quot;:1}}</span>
</code></pre></div><h4 id="postjson" tabindex="-1">postJSON <a class="header-anchor" href="#postjson" aria-hidden="true">#</a></h4><p>Finally <code>postJSON</code> is jQuery&#39;s missing equivalent to <code>$.getJSON</code>, but for POST&#39;s, eg:</p><div class="language-javascript"><pre><code>$<span class="token punctuation">.</span>ss<span class="token punctuation">.</span><span class="token function">postJSON</span><span class="token punctuation">(</span>url<span class="token punctuation">,</span> <span class="token punctuation">{</span>data<span class="token operator">:</span><span class="token number">1</span><span class="token punctuation">}</span><span class="token punctuation">,</span> <span class="token parameter">response</span> <span class="token operator">=&gt;</span> <span class="token operator">...</span><span class="token punctuation">,</span> <span class="token parameter">error</span> <span class="token operator">=&gt;</span> <span class="token operator">...</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><h2 id="customize-json-responses-on-the-fly" tabindex="-1">Customize JSON Responses on-the-fly <a class="header-anchor" href="#customize-json-responses-on-the-fly" aria-hidden="true">#</a></h2><p>The JSON and JSV Responses for all Services (inc. AutoQuery Services) can now be further customized with the new <code>?jsconfig</code> QueryString param which lets your Service consumers customize the returned JSON Response to their preference. This works similar to having wrapped your Service response in a <code>HttpResult</code> with a Custom <code>ResultScope</code> in the Service implementation to enable non-default customization of a Services response, e.g:</p><pre><code>/service?jsconfig=EmitLowercaseUnderscoreNames,ExcludeDefaultValues
</code></pre><p>Works similarly to:</p><div class="language-csharp"><pre><code><span class="token keyword">return</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">HttpResult</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token punctuation">{</span> TheKey <span class="token operator">=</span> <span class="token string">&quot;value&quot;</span><span class="token punctuation">,</span> Foo<span class="token operator">=</span><span class="token number">0</span> <span class="token punctuation">}</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
    ResultScope <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> JsConfig<span class="token punctuation">.</span><span class="token function">With</span><span class="token punctuation">(</span>
        <span class="token named-parameter punctuation">emitLowercaseUnderscoreNames</span><span class="token punctuation">:</span><span class="token boolean">true</span><span class="token punctuation">,</span> <span class="token named-parameter punctuation">excludeDefaultValues</span><span class="token punctuation">:</span><span class="token boolean">true</span><span class="token punctuation">)</span>
<span class="token punctuation">}</span><span class="token punctuation">;</span>
</code></pre></div><p>Which results in <strong>lowercase_underscore</strong> key names with any properties with <strong>default values removed</strong>:</p><pre><code>{&quot;the_key&quot;:&quot;value&quot;}
</code></pre><p>It also supports cascading server and client ResultScopes, with the client <code>?jsconfig</code> taking precedence.</p><p>Nearly all <code>JsConfig</code> scope options are supported other than delegates and complex type configuration properties.</p><h3 id="camel-humps-notation" tabindex="-1">Camel Humps Notation <a class="header-anchor" href="#camel-humps-notation" aria-hidden="true">#</a></h3><p>JsConfig also supports Camel Humps notation letting you target a configuration by just using the <strong>Uppercase Letters</strong> in the property name which is also case-insensitive so an equivalent shorter version of the above config can be:</p><pre><code>?jsconfig=ELUN,edv
</code></pre><p>Camel Humps also works with Enum Values so both these two configurations are the same:</p><pre><code>?jsconfig=DateHandler:UnixTime
?jsconfig=dh:ut
</code></pre><h3 id="custom-json-live-example" tabindex="-1">Custom JSON Live Example <a class="header-anchor" href="#custom-json-live-example" aria-hidden="true">#</a></h3><p>AutoQuery Viewer makes use of this feature in order to return human readable dates using the new <code>ISO8601DateOnly</code> DateHandler Enum Value as well as appending <code>ExcludeDefaultValues</code> when specifying custom fields so that any unpopulated value type properties with default values are excluded from the JSON Response. Here&#39;s a live example of this comparing the default Response with the customized JSON Response:</p><ul><li><a href="http://github.servicestack.net/repos.json?fields=Name,Homepage,Language,Updated_At" target="_blank" rel="noopener noreferrer">http://github.servicestack.net/repos.json?fields=Name,Homepage,Language,Updated_At</a></li><li><a href="http://github.servicestack.net/repos.json?fields=Name,Homepage,Language,Updated_At&amp;jsconfig=edv,dh:iso8601do" target="_blank" rel="noopener noreferrer">http://github.servicestack.net/repos.json?fields=Name,Homepage,Language,Updated_At&amp;jsconfig=edv,dh:iso8601do</a></li></ul><h3 id="custom-json-settings" tabindex="-1">Custom JSON Settings <a class="header-anchor" href="#custom-json-settings" aria-hidden="true">#</a></h3><p>The presence of a <strong>bool</strong> configuration property will be set to <code>true</code> unless they have a <code>false</code> or <code>0</code> value in which case they will be set to <code>false</code>, e.g:</p><pre><code>?jsconfig=ExcludeDefaultValues:false
</code></pre><p>For a quick reference the following <strong>bool</strong> customizations are supported:</p><table class="table"><thead><tr><th>Name</th><th>Alias</th></tr></thead><tr><td>EmitCamelCaseNames</td><td>eccn</td></tr><tr><td>EmitLowercaseUnderscoreNames</td><td>elun</td></tr><tr><td>IncludeNullValues</td><td>inv</td></tr><tr><td>IncludeNullValuesInDictionaries</td><td>invid</td></tr><tr><td>IncludeDefaultEnums</td><td>ide</td></tr><tr><td>IncludePublicFields</td><td>ipf</td></tr><tr><td>IncludeTypeInfo</td><td>iti</td></tr><tr><td>ExcludeTypeInfo</td><td>eti</td></tr><tr><td>ConvertObjectTypesIntoStringDictionary</td><td>cotisd</td></tr><tr><td>TreatEnumAsInteger</td><td>teai</td></tr><tr><td>TryToParsePrimitiveTypeValues</td><td>ttpptv</td></tr><tr><td>TryToParseNumericType</td><td>ttpnt</td></tr><tr><td>ThrowOnDeserializationError</td><td>tode</td></tr><tr><td>EscapeUnicode</td><td>eu</td></tr><tr><td>PreferInterfaces</td><td>pi</td></tr><tr><td>SkipDateTimeConversion</td><td>sdtc</td></tr><tr><td>AlwaysUseUtc</td><td>auu</td></tr><tr><td>AssumeUtc</td><td>au</td></tr><tr><td>AppendUtcOffset</td><td>auo</td></tr><tr><th colspan="2">DateHandler (dh)</th></tr><tr><td>TimestampOffset</td><td>to</td></tr><tr><td>DCJSCompatible</td><td>dcjsc</td></tr><tr><td>ISO8601</td><td>iso8601</td></tr><tr><td>ISO8601DateOnly</td><td>iso8601do</td></tr><tr><td>ISO8601DateTime</td><td>iso8601dt</td></tr><tr><td>RFC1123</td><td>rfc1123</td></tr><tr><td>UnixTime</td><td>ut</td></tr><tr><td>UnixTimeMs</td><td>utm</td></tr><tr><th colspan="2">TimeSpanHandler (tsh)</th></tr><tr><td>DurationFormat</td><td>df</td></tr><tr><td>StandardFormat</td><td>sf</td></tr><tr><th colspan="2">PropertyConvention (pc)</th></tr><tr><td>Strict</td><td>s</td></tr><tr><td>Lenient</td><td>l</td></tr></table><p>You can also create a scope from a string manually using the new <code>JsConfig.CreateScope()</code>, e.g:</p><div class="language-csharp"><pre><code><span class="token keyword">using</span> <span class="token punctuation">(</span>JsConfig<span class="token punctuation">.</span><span class="token function">CreateScope</span><span class="token punctuation">(</span><span class="token string">&quot;EmitLowercaseUnderscoreNames,ExcludeDefaultValues,dh:ut&quot;</span><span class="token punctuation">)</span><span class="token punctuation">)</span> 
<span class="token punctuation">{</span>
    <span class="token class-name"><span class="token keyword">var</span></span> json <span class="token operator">=</span> dto<span class="token punctuation">.</span><span class="token function">ToJson</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre></div><p>If you don&#39;t wish for consumers to be able to customize JSON responses this feature can be disabled with <code>Config.AllowJsConfig=false</code>.</p><h2 id="improved-support-for-multitenancy" tabindex="-1">Improved support for Multitenancy <a class="header-anchor" href="#improved-support-for-multitenancy" aria-hidden="true">#</a></h2><p>All built-in dependencies available from <code>Service</code> base class, AutoQuery, Razor View pages, etc are now resolved in a central overridable location in your AppHost.</p><p>This now lets you control which dependency is used based on the incoming Request for each Service by overriding any of the AppHost methods below, e.g. to change the DB Connection your Service uses you can override <code>GetDbConnection(IRequest)</code> in your AppHost.</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">virtual</span> <span class="token return-type class-name">IDbConnection</span> Db
<span class="token punctuation">{</span>
    <span class="token keyword">get</span> <span class="token punctuation">{</span> <span class="token keyword">return</span> db <span class="token operator">??</span> <span class="token punctuation">(</span>db <span class="token operator">=</span> HostContext<span class="token punctuation">.</span>AppHost<span class="token punctuation">.</span><span class="token function">GetDbConnection</span><span class="token punctuation">(</span>Request<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token keyword">public</span> <span class="token keyword">virtual</span> <span class="token return-type class-name">ICacheClient</span> Cache
<span class="token punctuation">{</span>
    <span class="token keyword">get</span> <span class="token punctuation">{</span> <span class="token keyword">return</span> cache <span class="token operator">??</span> <span class="token punctuation">(</span>cache <span class="token operator">=</span> HostContext<span class="token punctuation">.</span>AppHost<span class="token punctuation">.</span><span class="token function">GetCacheClient</span><span class="token punctuation">(</span>Request<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token keyword">public</span> <span class="token keyword">virtual</span> MemoryCacheClient LocalCache <span class="token comment">//New</span>
<span class="token punctuation">{</span>
    <span class="token keyword">get</span> <span class="token punctuation">{</span> <span class="token keyword">return</span> localCache <span class="token operator">??</span> <span class="token punctuation">(</span>localCache <span class="token operator">=</span> HostContext<span class="token punctuation">.</span>AppHost<span class="token punctuation">.</span><span class="token function">GetMemoryCacheClient</span><span class="token punctuation">(</span>Request<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token keyword">public</span> <span class="token keyword">virtual</span> <span class="token return-type class-name">IRedisClient</span> Redis
<span class="token punctuation">{</span>
    <span class="token keyword">get</span> <span class="token punctuation">{</span> <span class="token keyword">return</span> redis <span class="token operator">??</span> <span class="token punctuation">(</span>redis <span class="token operator">=</span> HostContext<span class="token punctuation">.</span>AppHost<span class="token punctuation">.</span><span class="token function">GetRedisClient</span><span class="token punctuation">(</span>Request<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token keyword">public</span> <span class="token keyword">virtual</span> <span class="token return-type class-name">IMessageProducer</span> MessageProducer
<span class="token punctuation">{</span>
    <span class="token keyword">get</span> <span class="token punctuation">{</span> <span class="token keyword">return</span> messageProducer <span class="token operator">??</span> <span class="token punctuation">(</span>messageProducer <span class="token operator">=</span> HostContext<span class="token punctuation">.</span>AppHost<span class="token punctuation">.</span><span class="token function">GetMessageProducer</span><span class="token punctuation">(</span>Request<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><h3 id="change-database-connection-at-runtime" tabindex="-1">Change Database Connection at Runtime <a class="header-anchor" href="#change-database-connection-at-runtime" aria-hidden="true">#</a></h3><p>The default implementation of <code>GetDbConnection(IRequest)</code> includes an easy way to change the DB Connection that can be done by populating the <a href="https://github.com/ServiceStack/ServiceStack/blob/master/src/ServiceStack/ConnectionInfo.cs" target="_blank" rel="noopener noreferrer">ConnectionInfo</a> POCO in any <a href="/order-of-operations.html">Request Filter in the Request Pipeline</a>:</p><div class="language-csharp"><pre><code>req<span class="token punctuation">.</span>Items<span class="token punctuation">[</span>Keywords<span class="token punctuation">.</span>DbInfo<span class="token punctuation">]</span> <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">ConnectionInfo</span> <span class="token punctuation">{</span>
    NamedConnection  <span class="token operator">=</span> <span class="token range operator">..</span><span class="token punctuation">.</span> <span class="token comment">//Use a registered NamedConnection for this Request</span>
    ConnectionString <span class="token operator">=</span> <span class="token range operator">..</span><span class="token punctuation">.</span> <span class="token comment">//Use a different DB connection for this Request</span>
    ProviderName     <span class="token operator">=</span> <span class="token range operator">..</span><span class="token punctuation">.</span> <span class="token comment">//Use a different Dialect Provider for this Request</span>
<span class="token punctuation">}</span><span class="token punctuation">;</span>
</code></pre></div><p>To illustrate how this works we&#39;ll go through a simple example showing how to create an AutoQuery Service that lets the user change which DB the Query is run on. We&#39;ll control which of the Services we want to allow the user to change the DB it&#39;s run on by having them implement the interface below:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">interface</span> <span class="token class-name">IChangeDb</span>
<span class="token punctuation">{</span>
    <span class="token return-type class-name"><span class="token keyword">string</span></span> NamedConnection <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token return-type class-name"><span class="token keyword">string</span></span> ConnectionString <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token return-type class-name"><span class="token keyword">string</span></span> ProviderName <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>We&#39;ll create one such AutoQuery Service, implementing the above interface:</p><div class="language-csharp"><pre><code><span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">Route</span><span class="token attribute-arguments"><span class="token punctuation">(</span><span class="token string">&quot;/rockstars&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">QueryRockstars</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">QueryBase<span class="token punctuation">&lt;</span>Rockstar<span class="token punctuation">&gt;</span></span><span class="token punctuation">,</span> <span class="token class-name">IChangeDb</span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span></span> NamedConnection <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span></span> ConnectionString <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span></span> ProviderName <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>For this example we&#39;ll configure our Database to use a default <strong>SQL Server 2012</strong> database, register an optional named connection looking at a &quot;Reporting&quot; <strong>PostgreSQL</strong> database and register an alternative <strong>Sqlite</strong> RDBMS Dialect that we also want the user to be able to use:</p><h4 id="changedb-apphost-registration" tabindex="-1">ChangeDB AppHost Registration <a class="header-anchor" href="#changedb-apphost-registration" aria-hidden="true">#</a></h4><div class="language-csharp"><pre><code>container<span class="token punctuation">.</span><span class="token generic-method"><span class="token function">Register</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>IDbConnectionFactory<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span>c <span class="token operator">=&gt;</span> 
    <span class="token keyword">new</span> <span class="token constructor-invocation class-name">OrmLiteConnectionFactory</span><span class="token punctuation">(</span>defaultDbConn<span class="token punctuation">,</span> SqlServer2012Dialect<span class="token punctuation">.</span>Provider<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token class-name"><span class="token keyword">var</span></span> dbFactory <span class="token operator">=</span> container<span class="token punctuation">.</span><span class="token generic-method"><span class="token function">Resolve</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>IDbConnectionFactory<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token comment">//Register NamedConnection</span>
dbFactory<span class="token punctuation">.</span><span class="token function">RegisterConnection</span><span class="token punctuation">(</span><span class="token string">&quot;Reporting&quot;</span><span class="token punctuation">,</span> ReportingConnString<span class="token punctuation">,</span> PostgreSqlDialect<span class="token punctuation">.</span>Provider<span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token comment">//Register DialectProvider</span>
dbFactory<span class="token punctuation">.</span><span class="token function">RegisterDialectProvider</span><span class="token punctuation">(</span><span class="token string">&quot;Sqlite&quot;</span><span class="token punctuation">,</span> SqliteDialect<span class="token punctuation">.</span>Provider<span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><h4 id="changedb-request-filter" tabindex="-1">ChangeDB Request Filter <a class="header-anchor" href="#changedb-request-filter" aria-hidden="true">#</a></h4><p>To enable this feature we just need to add a Request Filter that populates the <code>ConnectionInfo</code> with properties from the Request DTO:</p><div class="language-csharp"><pre><code>GlobalRequestFilters<span class="token punctuation">.</span><span class="token function">Add</span><span class="token punctuation">(</span><span class="token punctuation">(</span>req<span class="token punctuation">,</span> res<span class="token punctuation">,</span> dto<span class="token punctuation">)</span> <span class="token operator">=&gt;</span> <span class="token punctuation">{</span>
   <span class="token class-name"><span class="token keyword">var</span></span> changeDb <span class="token operator">=</span> dto <span class="token keyword">as</span> <span class="token class-name">IChangeDb</span><span class="token punctuation">;</span>
   <span class="token keyword">if</span> <span class="token punctuation">(</span>changeDb <span class="token operator">==</span> <span class="token keyword">null</span><span class="token punctuation">)</span> <span class="token keyword">return</span><span class="token punctuation">;</span>

   req<span class="token punctuation">.</span>Items<span class="token punctuation">[</span>Keywords<span class="token punctuation">.</span>DbInfo<span class="token punctuation">]</span> <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">ConnectionInfo</span> <span class="token punctuation">{</span>
       NamedConnection <span class="token operator">=</span> changeDb<span class="token punctuation">.</span>NamedConnection<span class="token punctuation">,</span>
       ConnectionString <span class="token operator">=</span> changeDb<span class="token punctuation">.</span>ConnectionString<span class="token punctuation">,</span>
       ProviderName <span class="token operator">=</span> changeDb<span class="token punctuation">.</span>ProviderName<span class="token punctuation">,</span>
   <span class="token punctuation">}</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><p>Since our <code>IChangeDb</code> interface shares the same property names as <code>ConnectionInfo</code>, the above code can be further condensed using a <a href="/request-and-response-filters.html#typed-request-filters">Typed Request Filter</a> and ServiceStack&#39;s built-in <a href="/auto-mapping.html">AutoMapping</a> down to just:</p><div class="language-csharp"><pre><code><span class="token generic-method"><span class="token function">RegisterTypedRequestFilter</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>IChangeDb<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token punctuation">(</span>req<span class="token punctuation">,</span> res<span class="token punctuation">,</span> dto<span class="token punctuation">)</span> <span class="token operator">=&gt;</span>
    req<span class="token punctuation">.</span>Items<span class="token punctuation">[</span>Keywords<span class="token punctuation">.</span>DbInfo<span class="token punctuation">]</span> <span class="token operator">=</span> dto<span class="token punctuation">.</span><span class="token generic-method"><span class="token function">ConvertTo</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>ConnectionInfo<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><h4 id="change-databases-via-querystring" tabindex="-1">Change Databases via QueryString <a class="header-anchor" href="#change-databases-via-querystring" aria-hidden="true">#</a></h4><p>With the above configuration the user can now change which database they want to execute the query on, e.g:</p><div class="language-csharp"><pre><code><span class="token class-name"><span class="token keyword">var</span></span> response <span class="token operator">=</span> client<span class="token punctuation">.</span><span class="token function">Get</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">QueryRockstars</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">//SQL Server</span>

<span class="token class-name"><span class="token keyword">var</span></span> response <span class="token operator">=</span> client<span class="token punctuation">.</span><span class="token function">Get</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">QueryRockstars</span> <span class="token punctuation">{</span>   <span class="token comment">//Reporting PostgreSQL DB</span>
    NamedConnection <span class="token operator">=</span> <span class="token string">&quot;Reporting&quot;</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span> 

<span class="token class-name"><span class="token keyword">var</span></span> response <span class="token operator">=</span> client<span class="token punctuation">.</span><span class="token function">Get</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">QueryRockstars</span> <span class="token punctuation">{</span>   <span class="token comment">//Alternative SQL Server Database</span>
    ConnectionString <span class="token operator">=</span> <span class="token string">&quot;Server=alt-host;Database=Rockstars;User Id=test;Password=test;&quot;</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span> 

<span class="token class-name"><span class="token keyword">var</span></span> response <span class="token operator">=</span> client<span class="token punctuation">.</span><span class="token function">Get</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">QueryRockstars</span> <span class="token punctuation">{</span>   <span class="token comment">//Alternative SQLite Database</span>
    ConnectionString <span class="token operator">=</span> <span class="token string">&quot;C:\\backups\\2016-01-01.sqlite&quot;</span><span class="token punctuation">,</span>
    ProviderName <span class="token operator">=</span> <span class="token string">&quot;Sqlite&quot;</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span> 
</code></pre></div><h3 id="connectioninfo-attribute" tabindex="-1">ConnectionInfo Attribute <a class="header-anchor" href="#connectioninfo-attribute" aria-hidden="true">#</a></h3><p>To make it even easier to use we&#39;ve also wrapped this feature in a simple <a href="https://github.com/ServiceStack/ServiceStack/blob/master/src/ServiceStack/ConnectionInfoAttribute.cs" target="_blank" rel="noopener noreferrer">ConnectionInfoAttribute.cs</a> which allows you to declaratively specify which database a Service should be configured to use, e.g we can configure the <code>Db</code> connection in the Service below to use the PostgreSQL <strong>Reporting</strong> database with:</p><div class="language-csharp"><pre><code><span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">ConnectionInfo</span><span class="token attribute-arguments"><span class="token punctuation">(</span>NamedConnection <span class="token operator">=</span> <span class="token string">&quot;Reporting&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">ReportingServices</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">Service</span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">object</span></span> <span class="token function">Any</span><span class="token punctuation">(</span><span class="token class-name">Sales</span> request<span class="token punctuation">)</span>
    <span class="token punctuation">{</span>
        <span class="token keyword">return</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">SalesResponse</span> <span class="token punctuation">{</span> Results <span class="token operator">=</span> Db<span class="token punctuation">.</span><span class="token generic-method"><span class="token function">Select</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>Sales<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">}</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><h3 id="multi-tenancy-example" tabindex="-1"><a href="https://github.com/ServiceStack/ServiceStack/blob/master/tests/ServiceStack.WebHost.Endpoints.Tests/MultiTennantAppHostTests.cs" target="_blank" rel="noopener noreferrer">Multi Tenancy Example</a> <a class="header-anchor" href="#multi-tenancy-example" aria-hidden="true">#</a></h3><p>To show how much easier it is to implement a Multi Tenancy Service with this feature we&#39;ve updated the <a href="https://github.com/ServiceStack/ServiceStack/blob/master/tests/ServiceStack.WebHost.Endpoints.Tests/MultiTennantAppHostTests.cs" target="_blank" rel="noopener noreferrer">Multi Tenancy AppHost Example</a> comparing it with the previous approach of implementing a Custom <code>IDbConnectionFactory</code>.</p><h3 id="new-createquery-overloads-for-custom-autoquery" tabindex="-1">New CreateQuery overloads for Custom AutoQuery <a class="header-anchor" href="#new-createquery-overloads-for-custom-autoquery" aria-hidden="true">#</a></h3><p>In order for AutoQuery to pass the current <code>IRequest</code> into the new <code>AppHost.GetDbConnection(IRequest)</code> method it needs to be passed when calling <code>CreateQuery</code>. 2 new API&#39;s have been added that now does this:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">MyServices</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">Service</span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name">IAutoQuery</span> AutoQuery <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>

    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">object</span></span> <span class="token function">Any</span><span class="token punctuation">(</span><span class="token class-name">Request</span> dto<span class="token punctuation">)</span>
    <span class="token punctuation">{</span>
        <span class="token class-name"><span class="token keyword">var</span></span> q <span class="token operator">=</span> AutoQuery<span class="token punctuation">.</span><span class="token function">CreateQuery</span><span class="token punctuation">(</span>dto<span class="token punctuation">,</span> <span class="token keyword">base</span><span class="token punctuation">.</span>Request<span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token comment">//Calls:</span>
        <span class="token comment">//var q = AutoQuery.CreateQuery(dto, base.Request.GetRequestParams(), base.Request);</span>
        <span class="token keyword">return</span> AutoQuery<span class="token punctuation">.</span><span class="token function">Execute</span><span class="token punctuation">(</span>request<span class="token punctuation">,</span> q<span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><h2 id="serviceclient-url-resolvers" tabindex="-1">ServiceClient URL Resolvers <a class="header-anchor" href="#serviceclient-url-resolvers" aria-hidden="true">#</a></h2><p>The urls used in all .NET Service Clients are now customizable with the new <code>UrlResolver</code> and <code>TypedUrlResolver</code> delegates.</p><p>E.g. you can use this feature to rewrite the URL used with the Request DTO Type Name used as the subdomain by:</p><div class="language-csharp"><pre><code><span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">Route</span><span class="token attribute-arguments"><span class="token punctuation">(</span><span class="token string">&quot;/test&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span> 
<span class="token keyword">class</span> <span class="token class-name">Request</span> <span class="token punctuation">{</span><span class="token punctuation">}</span>

<span class="token class-name"><span class="token keyword">var</span></span> client <span class="token operator">=</span> <span class="token function">JsonServiceClient</span><span class="token punctuation">(</span><span class="token string">&quot;http://example.org/api&quot;</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
    TypedUrlResolver <span class="token operator">=</span>  <span class="token punctuation">(</span>meta<span class="token punctuation">,</span> httpMethod<span class="token punctuation">,</span> dto<span class="token punctuation">)</span> <span class="token operator">=&gt;</span> 
        meta<span class="token punctuation">.</span>BaseUri<span class="token punctuation">.</span><span class="token function">Replace</span><span class="token punctuation">(</span><span class="token string">&quot;example.org&quot;</span><span class="token punctuation">,</span> dto<span class="token punctuation">.</span><span class="token function">GetType</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span>Name <span class="token operator">+</span> <span class="token string">&quot;.example.org&quot;</span><span class="token punctuation">)</span>
            <span class="token punctuation">.</span><span class="token function">CombineWith</span><span class="token punctuation">(</span>dto<span class="token punctuation">.</span><span class="token function">ToUrl</span><span class="token punctuation">(</span>httpMethod<span class="token punctuation">,</span> meta<span class="token punctuation">.</span>Format<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span><span class="token punctuation">;</span>

<span class="token class-name"><span class="token keyword">var</span></span> res <span class="token operator">=</span> client<span class="token punctuation">.</span><span class="token function">Get</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">Request</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>  <span class="token comment">//= http://Request.example.org/api/test</span>
<span class="token class-name"><span class="token keyword">var</span></span> res <span class="token operator">=</span> client<span class="token punctuation">.</span><span class="token function">Post</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">Request</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">//= http://Request.example.org/api/test</span>
</code></pre></div><p>This feature is also implemented in <code>JsonHttpClient</code>, examples below shows rewriting APIs that use custom urls:</p><div class="language-csharp"><pre><code><span class="token class-name"><span class="token keyword">var</span></span> client <span class="token operator">=</span> <span class="token function">JsonHttpClient</span><span class="token punctuation">(</span><span class="token string">&quot;http://example.org/api&quot;</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
    UrlResolver <span class="token operator">=</span> <span class="token punctuation">(</span>meta<span class="token punctuation">,</span> httpMethod<span class="token punctuation">,</span> url<span class="token punctuation">)</span> <span class="token operator">=&gt;</span> 
        meta<span class="token punctuation">.</span>BaseUri<span class="token punctuation">.</span><span class="token function">Replace</span><span class="token punctuation">(</span><span class="token string">&quot;example.org&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;111.111.111.111&quot;</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">CombineWith</span><span class="token punctuation">(</span>url<span class="token punctuation">)</span><span class="token punctuation">)</span>
<span class="token punctuation">}</span><span class="token punctuation">;</span>

<span class="token keyword">await</span> client<span class="token punctuation">.</span><span class="token generic-method"><span class="token function">DeleteAsync</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>MockResponse<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token string">&quot;/dummy&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span> 
<span class="token comment">//=http://111.111.111.111/api/dummy</span>

<span class="token keyword">await</span> client<span class="token punctuation">.</span><span class="token generic-method"><span class="token function">PutAsync</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>MockResponse<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token string">&quot;/dummy&quot;</span><span class="token punctuation">,</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">Request</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span> 
<span class="token comment">//=http://111.111.111.111/api/dummy</span>
</code></pre></div><h2 id="servicestack-discovery-consul" tabindex="-1"><a href="https://github.com/wwwlicious/servicestack-discovery-consul" target="_blank" rel="noopener noreferrer">ServiceStack.Discovery.Consul</a> <a class="header-anchor" href="#servicestack-discovery-consul" aria-hidden="true">#</a></h2><p>This feature was added to make it easier to support the new <a href="https://github.com/wwwlicious/servicestack-discovery-consul" target="_blank" rel="noopener noreferrer">ServiceStack.Discovery.Consul</a> plugin by <a href="https://twitter.com/wwwlicious" target="_blank" rel="noopener noreferrer">Scott Mackay</a> which enables external RequestDTO endpoint discovery by integrating with <a href="http://consul.io" target="_blank" rel="noopener noreferrer">Consul.io</a> to provide automatic service registration and health checking.</p><p><img src="https://raw.githubusercontent.com/wwwlicious/servicestack-discovery-consul/master/assets/RequestDTOServiceDiscovery.png" alt="RequestDTO Service Discovery"></p><p>To use the plugin install it from NuGet:</p><pre><code>Install-Package ServiceStack.Discovery.Consul
</code></pre><p>Then configure your AppHost specifying the external <code>WebHostUrl</code> for this Service as well as registering the <code>ConsulFeature</code> plugin:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">AppHost</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">AppSelfHostBase</span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token function">AppHost</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">:</span> <span class="token keyword">base</span><span class="token punctuation">(</span><span class="token string">&quot;MyService&quot;</span><span class="token punctuation">,</span> <span class="token keyword">typeof</span><span class="token punctuation">(</span><span class="token type-expression class-name">MyService</span><span class="token punctuation">)</span><span class="token punctuation">.</span>Assembly<span class="token punctuation">)</span> <span class="token punctuation">{</span><span class="token punctuation">}</span>

    <span class="token keyword">public</span> <span class="token keyword">override</span> <span class="token return-type class-name"><span class="token keyword">void</span></span> <span class="token function">Configure</span><span class="token punctuation">(</span><span class="token class-name">Container</span> container<span class="token punctuation">)</span>
    <span class="token punctuation">{</span>
        <span class="token function">SetConfig</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">HostConfig</span> <span class="token punctuation">{</span>
            <span class="token comment">// the url:port that other services will use to access this one</span>
            WebHostUrl <span class="token operator">=</span> <span class="token string">&quot;http://api.acme.com:1234&quot;</span>
            ApiVersion <span class="token operator">=</span> <span class="token string">&quot;2.0&quot;</span> <span class="token comment">// optional</span>
        <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

        <span class="token comment">// Pass in any ServiceClient and it will be autowired with Func</span>
        Plugins<span class="token punctuation">.</span><span class="token function">Add</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">ConsulFeature</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">JsonServiceClient</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>You&#39;ll also need to <a href="https://github.com/wwwlicious/servicestack-discovery-consul/blob/master/README.md#running-your-services" target="_blank" rel="noopener noreferrer">install and start a Consul agent</a> after which once the AppHost is initialized you should see it automatically appear in the <a href="https://www.consul.io/intro/getting-started/ui.html" target="_blank" rel="noopener noreferrer">Consul UI</a> and disappear after the AppHost is shutdown:</p><p><img src="https://raw.githubusercontent.com/wwwlicious/servicestack-discovery-consul/master/assets/ServiceRegistration.png" alt="Automatic Service Registration"></p><p>In your Services you&#39;ll then be able to use the Consul-injected <code>IServiceClient</code> to call any external services and it will automatically send the request to an active Service endpoint that handles each Request DTO, e.g:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">MyService</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">Service</span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name">IServiceClient</span> Client <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>

    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">void</span></span> <span class="token function">Any</span><span class="token punctuation">(</span><span class="token class-name">RequestDTO</span> dto<span class="token punctuation">)</span>
    <span class="token punctuation">{</span>
        <span class="token comment">// the client will resolve the correct uri for the external dto using consul</span>
        <span class="token class-name"><span class="token keyword">var</span></span> response <span class="token operator">=</span> Client<span class="token punctuation">.</span><span class="token function">Post</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">ExternalDTO</span> <span class="token punctuation">{</span> Custom <span class="token operator">=</span> <span class="token string">&quot;bob&quot;</span> <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><h4 id="health-checks" tabindex="-1">Health checks <a class="header-anchor" href="#health-checks" aria-hidden="true">#</a></h4><p><img src="https://raw.githubusercontent.com/wwwlicious/servicestack-discovery-consul/master/assets/HealthChecks.png" alt="Default Health Checks"></p><p>By default the plugin creates 2 health checks used to filter out failing instances of your services:</p><ol><li>Heartbeat: Creates an endpoint in your service <a href="http://locahost:1234/reply/json/heartbeat" target="_blank" rel="noopener noreferrer">http://locahost:1234/reply/json/heartbeat</a> that expects a 200 response</li><li>If Redis has been configured in the AppHost, it will check if Redis is responding</li></ol><p>For more info checkout <a href="https://github.com/wwwlicious/servicestack-discovery-consul/" target="_blank" rel="noopener noreferrer">servicestack-discovery-consul</a> GitHub Repo.</p><h2 id="multiple-file-uploads" tabindex="-1">Multiple File Uploads <a class="header-anchor" href="#multiple-file-uploads" aria-hidden="true">#</a></h2><p>New .NET APIs have been added to all .NET Service Clients that allow you to easily upload multiple streams within a single HTTP request. It supports populating Request DTO with any combination of QueryString and POST&#39;ed FormData in addition to multiple file upload data streams:</p><div class="language-csharp"><pre><code><span class="token keyword">using</span> <span class="token punctuation">(</span><span class="token class-name"><span class="token keyword">var</span></span> stream1 <span class="token operator">=</span> uploadFile1<span class="token punctuation">.</span><span class="token function">OpenRead</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
<span class="token keyword">using</span> <span class="token punctuation">(</span><span class="token class-name"><span class="token keyword">var</span></span> stream2 <span class="token operator">=</span> uploadFile2<span class="token punctuation">.</span><span class="token function">OpenRead</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
<span class="token punctuation">{</span>
    <span class="token class-name"><span class="token keyword">var</span></span> client <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">JsonServiceClient</span><span class="token punctuation">(</span>baseUrl<span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token class-name"><span class="token keyword">var</span></span> response <span class="token operator">=</span> client<span class="token punctuation">.</span><span class="token generic-method"><span class="token function">PostFilesWithRequest</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>MultipleFileUploadResponse<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span>
        <span class="token string">&quot;/multi-fileuploads?CustomerId=123&quot;</span><span class="token punctuation">,</span>
        <span class="token keyword">new</span> <span class="token constructor-invocation class-name">MultipleFileUpload</span> <span class="token punctuation">{</span> CustomerName <span class="token operator">=</span> <span class="token string">&quot;Foo,Bar&quot;</span> <span class="token punctuation">}</span><span class="token punctuation">,</span>
        <span class="token keyword">new</span><span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token punctuation">{</span>
            <span class="token keyword">new</span> <span class="token constructor-invocation class-name">UploadFile</span><span class="token punctuation">(</span><span class="token string">&quot;upload1.png&quot;</span><span class="token punctuation">,</span> stream1<span class="token punctuation">)</span><span class="token punctuation">,</span>
            <span class="token keyword">new</span> <span class="token constructor-invocation class-name">UploadFile</span><span class="token punctuation">(</span><span class="token string">&quot;upload2.png&quot;</span><span class="token punctuation">,</span> stream2<span class="token punctuation">)</span><span class="token punctuation">,</span>
        <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre></div><p>Or using only a Typed Request DTO. The <code>JsonHttpClient</code> also includes async equivalents for each of the new <code>PostFilesWithRequest</code> APIs:</p><div class="language-csharp"><pre><code><span class="token keyword">using</span> <span class="token punctuation">(</span><span class="token class-name"><span class="token keyword">var</span></span> stream1 <span class="token operator">=</span> uploadFile1<span class="token punctuation">.</span><span class="token function">OpenRead</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
<span class="token keyword">using</span> <span class="token punctuation">(</span><span class="token class-name"><span class="token keyword">var</span></span> stream2 <span class="token operator">=</span> uploadFile2<span class="token punctuation">.</span><span class="token function">OpenRead</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
<span class="token punctuation">{</span>
    <span class="token class-name"><span class="token keyword">var</span></span> client <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">JsonHttpClient</span><span class="token punctuation">(</span>baseUrl<span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token class-name"><span class="token keyword">var</span></span> response <span class="token operator">=</span> <span class="token keyword">await</span> client<span class="token punctuation">.</span><span class="token generic-method"><span class="token function">PostFilesWithRequestAsync</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>MultipleFileUploadResponse<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span>
        <span class="token keyword">new</span> <span class="token constructor-invocation class-name">MultipleFileUpload</span> <span class="token punctuation">{</span> CustomerId <span class="token operator">=</span> <span class="token number">123</span><span class="token punctuation">,</span> CustomerName <span class="token operator">=</span> <span class="token string">&quot;Foo,Bar&quot;</span> <span class="token punctuation">}</span><span class="token punctuation">,</span>
        <span class="token keyword">new</span><span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token punctuation">{</span>
            <span class="token keyword">new</span> <span class="token constructor-invocation class-name">UploadFile</span><span class="token punctuation">(</span><span class="token string">&quot;upload1.png&quot;</span><span class="token punctuation">,</span> stream1<span class="token punctuation">)</span><span class="token punctuation">,</span>
            <span class="token keyword">new</span> <span class="token constructor-invocation class-name">UploadFile</span><span class="token punctuation">(</span><span class="token string">&quot;upload2.png&quot;</span><span class="token punctuation">,</span> stream2<span class="token punctuation">)</span><span class="token punctuation">,</span>
        <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre></div><p>Special thanks to <a href="https://github.com/rsafier" target="_blank" rel="noopener noreferrer">@rsafier</a> for contributing support for Multiple File Uploads.</p><h3 id="pcl-winstore-client-retargeted-to-8-1" tabindex="-1">PCL WinStore Client retargeted to 8.1 <a class="header-anchor" href="#pcl-winstore-client-retargeted-to-8-1" aria-hidden="true">#</a></h3><p>Following a <a href="http://VS.NET" target="_blank" rel="noopener noreferrer">VS.NET</a> Update, we&#39;ve upgraded the <strong>WinStore</strong> PCL ServiceStack.Client to target <strong>8.1</strong>.</p><h3 id="local-memorycacheclient" tabindex="-1">Local MemoryCacheClient <a class="header-anchor" href="#local-memorycacheclient" aria-hidden="true">#</a></h3><p>As it sometimes beneficial to have access to a local in-memory Cache in addition to your registered <code>ICacheClient</code><a href="/caching.html">Caching Provider</a> we&#39;ve pre-registered a <code>MemoryCacheClient</code> that all your Services now have access to from the <code>LocalCache</code> property, i.e:</p><div class="language-csharp"><pre><code>    <span class="token return-type class-name">MemoryCacheClient</span> LocalCache <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
</code></pre></div><p>This doesn&#39;t affect any existing functionality that utilizes a cache like Sessions which continue to use your registered <code>ICacheClient</code>, but it does let you change which cache you want different responses to use, e.g:</p><div class="language-csharp"><pre><code><span class="token class-name"><span class="token keyword">var</span></span> cacheKey <span class="token operator">=</span> <span class="token string">&quot;unique_key_for_this_request&quot;</span><span class="token punctuation">;</span>
<span class="token keyword">return</span> <span class="token keyword">base</span><span class="token punctuation">.</span>Request<span class="token punctuation">.</span><span class="token function">ToOptimizedResultUsingCache</span><span class="token punctuation">(</span>LocalCache<span class="token punctuation">,</span> cacheKey<span class="token punctuation">,</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> <span class="token punctuation">{</span>
    <span class="token comment">//Delegate is executed if item doesn&#39;t exist in cache </span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><p>If you don&#39;t register a <code>ICacheClient</code> ServiceStack automatically registers a <code>MemoryCacheClient</code> for you which will also refer to the same instance registered for <code>LocalCache</code>.</p><h3 id="cookies" tabindex="-1">Cookies <a class="header-anchor" href="#cookies" aria-hidden="true">#</a></h3><p>If you&#39;re using a Custom <code>AuthProvider</code> that doesn&#39;t rely on Session Cookies you can disable them from being created with <code>Config.AllowSessionCookies=false</code>. The Cookie behavior can be further customized by overriding <code>AllowSetCookie()</code> in your AppHost, E.g. you can disable all cookies with:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">override</span> <span class="token return-type class-name"><span class="token keyword">bool</span></span> <span class="token function">AllowSetCookie</span><span class="token punctuation">(</span><span class="token class-name">IRequest</span> req<span class="token punctuation">,</span> <span class="token class-name"><span class="token keyword">string</span></span> cookieName<span class="token punctuation">)</span>
<span class="token punctuation">{</span>
    <span class="token keyword">return</span> <span class="token boolean">false</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre></div><h2 id="redis" tabindex="-1">Redis <a class="header-anchor" href="#redis" aria-hidden="true">#</a></h2><ul><li><a href="https://github.com/ServiceStack/ServiceStack.Redis/commit/d6ed687a1f25cec0f43fb04e3c906905b5c99085" target="_blank" rel="noopener noreferrer">Added support for HashSet API&#39;s in Redis Transactions</a></li></ul><h2 id="ormlite" tabindex="-1">OrmLite <a class="header-anchor" href="#ormlite" aria-hidden="true">#</a></h2><p>New <code>[EnumAsInt]</code> attribute as an alternative to <code>[Flags]</code> for storing Enums as ints in OrmLite but still have them serialized as strings in Service responses.</p><p>Free-text SQL Expressions are now converted to Parameterized Statements, e.g:</p><div class="language-csharp"><pre><code><span class="token class-name"><span class="token keyword">var</span></span> q <span class="token operator">=</span> db<span class="token punctuation">.</span><span class="token generic-method"><span class="token function">From</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>Rockstar<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token punctuation">.</span><span class="token function">Where</span><span class="token punctuation">(</span><span class="token string">&quot;Id &lt; {0} AND Age = {1}&quot;</span><span class="token punctuation">,</span> <span class="token number">3</span><span class="token punctuation">,</span> <span class="token number">27</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token class-name"><span class="token keyword">var</span></span> results <span class="token operator">=</span> db<span class="token punctuation">.</span><span class="token function">Select</span><span class="token punctuation">(</span>q<span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><h3 id="select-fields" tabindex="-1">Select Fields <a class="header-anchor" href="#select-fields" aria-hidden="true">#</a></h3><p>The new Select API on SqlExpression enables a resilient way to select custom fields matching the first column it finds (from primary table then joined tables). It also fully qualifies field names to avoid ambiguous columns, allows matching of joined tables with <code>{Table}{Column}</code> convention and ignores any non-matching fields, e.g:</p><div class="language-csharp"><pre><code><span class="token class-name"><span class="token keyword">var</span></span> q <span class="token operator">=</span> db<span class="token punctuation">.</span><span class="token generic-method"><span class="token function">From</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>Rockstar<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token punctuation">.</span><span class="token generic-method"><span class="token function">Join</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>RockstarAlbum<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token punctuation">(</span>r<span class="token punctuation">,</span>a<span class="token punctuation">)</span> <span class="token operator">=&gt;</span> r<span class="token punctuation">.</span>Id <span class="token operator">==</span> a<span class="token punctuation">.</span>RockstarId<span class="token punctuation">)</span>
    <span class="token punctuation">.</span><span class="token function">Select</span><span class="token punctuation">(</span><span class="token keyword">new</span><span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token punctuation">{</span> <span class="token string">&quot;Id&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;FirstName&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;Age&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;RockstarAlbumName&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;_unknown_&quot;</span> <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><h3 id="other-ormlite-changes" tabindex="-1">Other OrmLite Changes <a class="header-anchor" href="#other-ormlite-changes" aria-hidden="true">#</a></h3><ul><li>All <code>db.Exists()</code> APIs have been optimized to only query a single column and row.</li><li>New <code>db.SelectLazy()</code> API added that accepts an SqlExpression</li><li>Max String column definition for MySQL now uses <strong>LONGTEXT</strong></li></ul><h3 id="servicestack-text" tabindex="-1">ServiceStack.Text <a class="header-anchor" href="#servicestack-text" aria-hidden="true">#</a></h3><ul><li>New <code>JsConfig.SkipDateTimeConversion</code> to skip built-in Conversion of DateTime&#39;s.</li><li>New <code>ISO8601DateOnly</code> and <code>ISO8601DateTime</code> DateHandler formats to emit only the Date or Date and Time</li></ul><h2 id="stripe-gateway" tabindex="-1">Stripe Gateway <a class="header-anchor" href="#stripe-gateway" aria-hidden="true">#</a></h2><p>Added support for serializing nested complex entities using Stripe&#39;s unconventional object notation and the new <code>CreateStripeAccount</code> requiring it, e.g:</p><div class="language-csharp"><pre><code><span class="token class-name"><span class="token keyword">var</span></span> response <span class="token operator">=</span> gateway<span class="token punctuation">.</span><span class="token function">Post</span><span class="token punctuation">(</span><span class="token keyword">new</span> <span class="token constructor-invocation class-name">CreateStripeAccount</span>
<span class="token punctuation">{</span>
    Country <span class="token operator">=</span> <span class="token string">&quot;US&quot;</span><span class="token punctuation">,</span>
    Email <span class="token operator">=</span> <span class="token string">&quot;test@email.com&quot;</span><span class="token punctuation">,</span>
    Managed <span class="token operator">=</span> <span class="token boolean">true</span><span class="token punctuation">,</span>
    LegalEntity <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">StripeLegalEntity</span>
    <span class="token punctuation">{</span>
        Address <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">StripeAddress</span>
        <span class="token punctuation">{</span>
            Line1 <span class="token operator">=</span> <span class="token string">&quot;1 Highway Rd&quot;</span><span class="token punctuation">,</span>
            City <span class="token operator">=</span> <span class="token string">&quot;Brooklyn&quot;</span><span class="token punctuation">,</span>
            State <span class="token operator">=</span> <span class="token string">&quot;NY&quot;</span><span class="token punctuation">,</span>
            Country <span class="token operator">=</span> <span class="token string">&quot;US&quot;</span><span class="token punctuation">,</span>
            PostalCode <span class="token operator">=</span> <span class="token string">&quot;90210&quot;</span><span class="token punctuation">,</span>
        <span class="token punctuation">}</span><span class="token punctuation">,</span>
        Dob <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token constructor-invocation class-name">StripeDate</span><span class="token punctuation">(</span><span class="token number">1980</span><span class="token punctuation">,</span> <span class="token number">1</span><span class="token punctuation">,</span> <span class="token number">1</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
        BusinessName <span class="token operator">=</span> <span class="token string">&quot;Business Name&quot;</span><span class="token punctuation">,</span>
        FirstName <span class="token operator">=</span> <span class="token string">&quot;First&quot;</span><span class="token punctuation">,</span>
        LastName <span class="token operator">=</span> <span class="token string">&quot;Last&quot;</span><span class="token punctuation">,</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><p>Which sends a POST Form Data request that serializes the nested Dob into the object notation Stripe expects, e.g:</p><pre><code>&amp;legal_entity[dob][year]=1970&amp;legal_entity[dob][month]=1&amp;legal_entity[dob][day]=1 
</code></pre><h2 id="minor-servicestack-features" tabindex="-1">Minor ServiceStack Features <a class="header-anchor" href="#minor-servicestack-features" aria-hidden="true">#</a></h2><ul><li>Old Session removed and invalided when generating new session ids for a new AuthRequest</li><li>New ResourcesResponseFilter, ApiDeclarationFilter and OperationFilter added to SwaggerFeature to modify response</li><li><code>Name</code> property added to <code>IHttpFiles</code> in Response.Files collection</li><li><code>HostType</code>, <code>RootDirectoryPath</code>, <code>RequestAttributes</code>, <code>Ipv4Addresses</code> and <code>Ipv6Addresses</code> added to <a href="/debugging.html#request-info">?debug=requestinfo</a></li><li><code>StaticFileHandler</code> now has <code>IVirtualFile</code> and <code>IVirtualDirectory</code> constructor overloads</li><li>New <code>StaticContentHandler</code> for returning custom text or binary responses in <code>RawHttpHandlers</code></li></ul><h2 id="changes" tabindex="-1">Changes <a class="header-anchor" href="#changes" aria-hidden="true">#</a></h2><p><code>IRequest.GetRequestParams()</code> Dictionary now returns any duplicate fields with a hash + number suffix, e.g: <code>#1</code>. To retain existing behavior where duplicate values are merged into a <code>,</code> delimited string use <code>IRequest.GetFlattenedRequestParams()</code></p><p>HttpListener now returns the <code>RemoteEndPoint</code> as the <code>UserHostAddress</code> matching the <code>UserHostAddress</code> returned in <a href="http://ASP.NET" target="_blank" rel="noopener noreferrer">ASP.NET</a> Web Applications.</p><h2 id="warning-net-4-0-builds-will-cease-after-august-1-2016" tabindex="-1">WARNING .NET 4.0 builds will cease after August 1, 2016 <a class="header-anchor" href="#warning-net-4-0-builds-will-cease-after-august-1-2016" aria-hidden="true">#</a></h2><p>Microsoft has <a href="https://blogs.msdn.microsoft.com/dotnet/2015/12/09/support-ending-for-the-net-framework-4-4-5-and-4-5-1/" target="_blank" rel="noopener noreferrer">discontinued supporting .NET 4.0, 4.5 and 4.5.1</a> as of January 12th, 2016. We&#39;ve already started seeing a number of 3rd Party NuGet packages already drop support for .NET 4.0 builds which has kept us referencing old versions. As a result we intend to follow and stop providing .NET 4.0 builds ourselves after August 1st, 2016. If you absolutely need access to .NET 4.0 builds after this date please <a href="https://servicestack.uservoice.com/forums/176786-feature-requests/suggestions/12528912-continue-supporting-net-4-0-projects" target="_blank" rel="noopener noreferrer">leave a comment on this UserVoice entry</a>.</p><h1 id="v4-0-52-release-notes" tabindex="-1"><a href="/releases/v4.0.52.html">v4.0.52 Release Notes</a> <a class="header-anchor" href="#v4-0-52-release-notes" aria-hidden="true">#</a></h1>__VP_STATIC_END__`,288),p=[o];function c(r,i,l,u,k,d){return a(),s("div",null,p)}var g=n(t,[["render",c]]);export{m as __pageData,g as default};
